    41
    42    // Deterministic fallback ranking
    43    const recommendations = deterministicRanking(
    44      eligibleRackets.length > 0 ? eligibleRackets : rackets,
    45      level,
    46      weaknesses,
    47      style_pref
    48    );
    49
    50    return NextResponse.json({ recommendations });
