'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from 'recharts';
import DashboardNav from '@/components/DashboardNav';
import { supabase } from '@/lib/supabase';
import type { Session, ScoreSummary } from '@/lib/types';

// Generate mock data for demo
function generateMockData() {
  const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
  return days.map((day, i) => ({
    day,
    score: Math.floor(60 + Math.random() * 30),
    greenRatio: Math.floor(50 + Math.random() * 40),
    checkIns: Math.floor(1 + Math.random() * 4),
  }));
}

export default function DashboardPage() {
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [sessions, setSessions] = useState<Session[]>([]);
  const [chartData, setChartData] = useState(generateMockData());
  const [stats, setStats] = useState({
    totalSessions: 12,
    avgScore: 74,
    greenRatio: 68,
    totalPracticeTime: 4.5,
  });

  useEffect(() => {
    checkAuth();
    loadSessions();
  }, []);

  const checkAuth = async () => {
    const { data: { user } } = await supabase.auth.getUser();
    if (!user) {
      // Allow demo mode without auth
      setLoading(false);
    }
  };

  const loadSessions = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        setLoading(false);
        return;
      }

      const { data, error } = await supabase
        .from('sessions')
        .select('*')
        .eq('user_id', user.id)
        .order('started_at', { ascending: false })
        .limit(20);

      if (error) throw error;

      if (data && data.length > 0) {
        setSessions(data);

        // Calculate stats from real data
        const totalGreen = data.reduce((sum, s) => sum + ((s.score_summary as ScoreSummary)?.green_count || 0), 0);
        const totalFrames = data.reduce((sum, s) => sum + ((s.score_summary as ScoreSummary)?.total_frames || 0), 0);
        const avgScore = data.reduce((sum, s) => sum + ((s.score_summary as ScoreSummary)?.average_score || 0), 0) / data.length;

        setStats({
          totalSessions: data.length,
          avgScore: Math.round(avgScore) || 74,
          greenRatio: totalFrames > 0 ? Math.round((totalGreen / totalFrames) * 100) : 68,
          totalPracticeTime: data.length * 0.5, // Estimate 30 min per session
        });

        // Convert to chart data
        const chartDataFromSessions = data.slice(0, 7).reverse().map((s, i) => {
          const summary = s.score_summary as ScoreSummary;
          return {
            day: new Date(s.started_at).toLocaleDateString('en-US', { weekday: 'short' }),
            score: summary?.average_score || Math.floor(60 + Math.random() * 30),
            greenRatio: summary?.total_frames
              ? Math.round((summary.green_count / summary.total_frames) * 100)
              : Math.floor(50 + Math.random() * 40),

