'use client';

import { useState, useEffect, useRef, useCallback, Suspense } from 'react';
import { useSearchParams } from 'next/navigation';
import DashboardNav from '@/components/DashboardNav';
import { supabase } from '@/lib/supabase';
import {
  extractMetrics,
  drawSkeleton,
  generateMockMetrics,
  evaluateMetrics,
  DEFAULT_THRESHOLDS,
} from '@/lib/pose-utils';
import type { PoseMetrics, PoseLandmark, TickResponse } from '@/lib/types';

// MediaPipe Tasks Vision types
type PoseLandmarker = any;
type FilesetResolver = any;

function PracticeContent() {
  const searchParams = useSearchParams();
  const drillId = searchParams.get('drill');

  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const poseLandmarkerRef = useRef<PoseLandmarker | null>(null);
  const animationRef = useRef<number | null>(null);
  const tickIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const sessionIdRef = useRef<string | null>(null);
  const lastVideoTimeRef = useRef<number>(-1);

  const [isActive, setIsActive] = useState(false);
  const [isPoseLoaded, setPoseLoaded] = useState(false);
  const [poseError, setPoseError] = useState<string | null>(null);
  const [useMockData, setUseMockData] = useState(false);

  const [currentMetrics, setCurrentMetrics] = useState<PoseMetrics | null>(null);
  const [isGreen, setIsGreen] = useState(true);
  const [coachingCue, setCoachingCue] = useState('Get ready to start!');
  const [focusMetric, setFocusMetric] = useState('');

  const [greenCount, setGreenCount] = useState(0);
  const [redCount, setRedCount] = useState(0);
  const [sessionStartTime, setSessionStartTime] = useState<Date | null>(null);

  const [thresholds, setThresholds] = useState(DEFAULT_THRESHOLDS);

  // Initialize MediaPipe Pose Landmarker
  useEffect(() => {
    initializePose();
    return () => {
      cleanup();
    };
  }, []);

  const initializePose = async () => {
    try {
      // Dynamically import MediaPipe Tasks Vision
      const vision = await import('@mediapipe/tasks-vision');
      const { PoseLandmarker, FilesetResolver } = vision;

      // Load the vision WASM files
      const filesetResolver = await FilesetResolver.forVisionTasks(
        'https://cdn.jsdelivr.net/npm/@mediapipe/tasks-vision@latest/wasm'
      );

      // Create the pose landmarker
      poseLandmarkerRef.current = await PoseLandmarker.createFromOptions(filesetResolver, {
        baseOptions: {
          modelAssetPath: 'https://storage.googleapis.com/mediapipe-models/pose_landmarker/pose_landmarker_lite/float16/1/pose_landmarker_lite.task',
          delegate: 'GPU',
        },
        runningMode: 'VIDEO',
        numPoses: 1,
        minPoseDetectionConfidence: 0.5,
        minPosePresenceConfidence: 0.5,
        minTrackingConfidence: 0.5,
      });

      setPoseLoaded(true);
    } catch (error) {
      console.error('Failed to load MediaPipe Pose:', error);
      setPoseError('Pose detection failed to load. Demo mode enabled.');
      setUseMockData(true);
      setPoseLoaded(true);
    }
  };

  const cleanup = () => {
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
    if (tickIntervalRef.current) {
      clearInterval(tickIntervalRef.current);
    }
    if (poseLandmarkerRef.current) {
      poseLandmarkerRef.current.close();
      poseLandmarkerRef.current = null;
    }
    stopCamera();
