    14→// Gemini 3 thinking tokens can make response.text contain non-JSON prefixes.
    15→// This helper robustly extracts JSON from potentially wrapped output.
    16→function safeParseJSON(text: string): any {
    17→  const raw = text.trim();
    18→  try { return JSON.parse(raw); } catch {}
    19→  // Try extracting from markdown code fences
    20→  const fenceMatch = raw.match(/```(?:json)?\s*([\s\S]*?)```/);
    21→  if (fenceMatch) { try { return JSON.parse(fenceMatch[1].trim()); } catch {} }
    22→  // Try finding first { to last }
    23→  const start = raw.indexOf('{');
    24→  const end = raw.lastIndexOf('}');
    25→  if (start !== -1 && end > start) {
    26→    try { return JSON.parse(raw.slice(start, end + 1)); } catch {}
    27→  }
    28→  // Try array
    29→  const aStart = raw.indexOf('[');
    30→  const aEnd = raw.lastIndexOf(']');
    31→  if (aStart !== -1 && aEnd > aStart) {
    32→    try { return JSON.parse(raw.slice(aStart, aEnd + 1)); } catch {}
    33→  }
    34→  return {};
    35→}
