   403→// Fallback racket recommendations
   404→function getFallbackRacketRecommendations(
   405→  rackets: Racket[],
   406→  level: string,
   407→  stylePref?: string
   408→): RacketRecommendation[] {
   409→  // Simple scoring based on matching criteria
   410→  const scored = rackets.map((racket) => {
   411→    let score = 50;
   412→
   413→    // Level match
   414→    if (racket.skill_level.includes(level)) score += 20;
   415→
   416→    // Style match
   417→    if (stylePref && racket.play_style.includes(stylePref)) score += 15;
   418→
   419→    // Beginner-friendly adjustments
   420→    if (level === 'beginner') {
   421→      if (racket.flexibility === 'Flexible') score += 10;
   422→      if (racket.price_band === 'budget') score += 5;
   423→    }
   424→
   425→    // Advanced adjustments
   426→    if (level === 'advanced') {
   427→      if (racket.flexibility === 'Stiff') score += 10;
   428→      if (racket.price_band === 'premium') score += 5;
   429→    }
   430→
   431→    return {
   432→      ...racket,
   433→      match_score: Math.min(score, 100),
   434→      match_reason: `Good match for ${level} players seeking ${stylePref || 'versatile'} play.`,
   435→    };
   436→  });
   437→
   438→  return scored
   439→    .sort((a, b) => b.match_score - a.match_score)
   440→    .slice(0, 5);
   441→}
