   363→          // Legs
   364→          ctx.beginPath();
   365→          ctx.moveTo(cx - 30, cy + 100);
   366→          ctx.lineTo(cx, cy + 40);
   367→          ctx.lineTo(cx + 30, cy + 100);
   368→          ctx.stroke();
   369→        }
   370→      }
   371→    } else if (poseLandmarkerRef.current && videoRef.current && canvasRef.current) {
   372→      const video = videoRef.current;
   373→      const canvas = canvasRef.current;
   374→      const ctx = canvas.getContext('2d');
   375→
   376→      // Only process if we have a new frame
   377→      if (video.currentTime !== lastVideoTimeRef.current && ctx) {
   378→        lastVideoTimeRef.current = video.currentTime;
   379→
   380→        try {
   381→          // PHASE 1 FIX: Ensure timestamps are strictly monotonically increasing
   382→          // to prevent MediaPipe "Packet timestamp mismatch" errors
   383→          const rawTimestampMs = Math.floor(performance.now());
   384→          const timestampMs = Math.max(lastTimestampMsRef.current + 1, rawTimestampMs);
   385→          lastTimestampMsRef.current = timestampMs;
   386→
   387→          // Detect pose
   388→          const results = poseLandmarkerRef.current.detectForVideo(video, timestampMs);
   389→
   390→          // Clear canvas (transparent background - video shows through)
   391→          ctx.clearRect(0, 0, canvas.width, canvas.height);
