   393→          if (results.landmarks && results.landmarks.length > 0) {
   394→            const poseLandmarks = results.landmarks[0];
   395→
   396→            // Convert to our format
   397→            const landmarks: PoseLandmark[] = poseLandmarks.map((lm: any) => ({
   398→              x: lm.x,
   399→              y: lm.y,
   400→              z: lm.z,
   401→              visibility: lm.visibility ?? 1.0,
   402→            }));
   403→
   404→            // Store current landmarks
   405→            currentLandmarksRef.current = landmarks;
   406→
   407→            const metrics = extractMetrics(landmarks);
   408→            setCurrentMetrics(metrics);
   409→
   410→            // Use rules engine for evaluation
   411→            const drillType = currentDrill?.keyframeType || 'general';
   412→            const evaluation = evaluateForm(landmarks, drillType, canvas.width);
