   414→
   415→            // Update green/red state
   416→            const green = evaluation.passed;
   417→            setIsGreen(green);
   418→
   419→            // Track failing rules
   420→            if (evaluation.failedRules.length > 0) {
   421→              setFailingRules(evaluation.failedRules.map(f => f.feedback));
   422→              setFocusMetric(evaluation.failedRules[0].rule.name);
   423→            } else {
   424→              setFailingRules([]);
   425→              setFocusMetric('');
   426→            }
   427→
   428→            // Update counts
   429→            if (green) {
   430→              setGreenCount((c) => c + 1);
   431→            } else {
   432→              setRedCount((c) => c + 1);
   433→            }
   434→
   435→            // Draw skeleton if enabled
   436→            if (showSkeletonRef.current) {
   437→              drawSkeleton(ctx, landmarks, green, canvas.width, canvas.height);
   438→            }
   439→          }
   440→        } catch (error) {
   441→          console.error('Error processing frame:', error);
   442→        }
   443→      }
   444→    }
