     1→import { NextResponse } from 'next/server';
     2→import { getCoachingCue } from '@/lib/gemini';
     3→import { evaluateMetrics, DEFAULT_THRESHOLDS } from '@/lib/pose-utils';
     4→import type { TickRequest, TickResponse, PoseMetrics } from '@/lib/types';
     5→
     6→// Cache for rate limiting Gemini calls
     7→let lastGeminiCall = 0;
     8→const GEMINI_CALL_INTERVAL = 2000; // Minimum 2 seconds between Gemini calls
     9→let cachedResponse: { cue: string; focus_metric: string; commentary?: string; reason?: string; latency_ms?: number } | null = null;
    10→
    11→export async function POST(request: Request) {
    12→  try {
    13→    const body: TickRequest = await request.json();
    14→    const { metrics_snapshot, drill_id } = body;
    15→
    16→    if (!metrics_snapshot) {
    17→      return NextResponse.json(
    18→        { error: 'metrics_snapshot is required' },
    19→        { status: 400 }
    20→      );
    21→    }
    22→
    23→    const metrics: PoseMetrics = metrics_snapshot;
    24→
    25→    // Evaluate metrics locally first
    26→    const { isGreen, feedback } = evaluateMetrics(metrics);
    27→
    28→    // Determine if we should call Gemini (rate limited)
    29→    const now = Date.now();
    30→    const shouldCallGemini = now - lastGeminiCall > GEMINI_CALL_INTERVAL;
    31→
    32→    let cue = feedback[0];
    33→    let focusMetric = determineFocusMetric(metrics);
    34→    let commentary = '';
    35→    let reason = '';
    36→    let latency_ms: number | undefined;
    37→
    38→    if (shouldCallGemini && process.env.GEMINI_API_KEY) {
    39→      try {
    40→        lastGeminiCall = now;
    41→
    42→        // Sanitize drill_id to prevent prompt injection
    43→        const safeDrillId = drill_id && typeof drill_id === 'string'
    44→          ? drill_id.replace(/[^a-zA-Z0-9_-]/g, '').slice(0, 50)
    45→          : null;
    46→        const drillContext = safeDrillId
    47→          ? `Practicing drill: ${safeDrillId}`
    48→          : undefined;
    49→
    50→        const geminiResult = await getCoachingCue(metrics, drillContext);
    51→        cue = geminiResult.cue;
    52→        focusMetric = geminiResult.focus_metric;
    53→        commentary = geminiResult.commentary;
    54→        reason = geminiResult.reason;
    55→        latency_ms = geminiResult.latency_ms;
    56→
    57→        // Cache the response
    58→        cachedResponse = { cue, focus_metric: focusMetric, commentary, reason, latency_ms };
    59→      } catch (error) {
    60→        console.error('Gemini error, using fallback:', error);
    61→        if (cachedResponse) {
    62→          cue = cachedResponse.cue;
    63→          focusMetric = cachedResponse.focus_metric;
    64→          commentary = cachedResponse.commentary || '';
    65→          reason = cachedResponse.reason || '';
    66→          latency_ms = cachedResponse.latency_ms;
    67→        }
    68→      }
    69→    } else if (cachedResponse && !shouldCallGemini) {
    70→      cue = cachedResponse.cue;
    71→      focusMetric = cachedResponse.focus_metric;
    72→      commentary = cachedResponse.commentary || '';
    73→      reason = cachedResponse.reason || '';
    74→      latency_ms = cachedResponse.latency_ms;
    75→    }
    76→
    77→    const response: TickResponse = {
    78→      cue,
    79→      focus_metric: focusMetric,
    80→      green_thresholds: DEFAULT_THRESHOLDS,
    81→      is_green: isGreen,
    82→      commentary,
    83→      reason,
    84→      latency_ms,
    85→    };
    86→
    87→    return NextResponse.json(response);
    88→  } catch (error) {
    89→    console.error('Practice tick error:', error);
    90→    return NextResponse.json(
    91→      { error: 'Failed to process tick' },
    92→      { status: 500 }
    93→    );
    94→  }
    95→}
    96→
    97→function determineFocusMetric(metrics: PoseMetrics): string {
    98→  const issues: { metric: string; deviation: number }[] = [];
    99→
   100→  // Check elbow angle (ideal: 90-120)
   101→  if (metrics.elbow_angle < 90) {
   102→    issues.push({
   103→      metric: 'elbow_angle',
   104→      deviation: 90 - metrics.elbow_angle,
   105→    });
   106→  } else if (metrics.elbow_angle > 120) {
   107→    issues.push({
   108→      metric: 'elbow_angle',
   109→      deviation: metrics.elbow_angle - 120,
   110→    });
   111→  }
   112→
   113→  // Check knee angle (ideal: 120-150)
   114→  if (metrics.knee_angle < 120) {
   115→    issues.push({
   116→      metric: 'knee_angle',
   117→      deviation: 120 - metrics.knee_angle,
   118→    });
   119→  } else if (metrics.knee_angle > 150) {
   120→    issues.push({
   121→      metric: 'knee_angle',
   122→      deviation: metrics.knee_angle - 150,
   123→    });
   124→  }
   125→
   126→  // Check stance width (ideal: 0.3-0.5)
   127→  if (metrics.stance_width_norm < 0.3) {
   128→    issues.push({
   129→      metric: 'stance_width_norm',
   130→      deviation: (0.3 - metrics.stance_width_norm) * 100, // Scale up for comparison
   131→    });
   132→  } else if (metrics.stance_width_norm > 0.5) {
   133→    issues.push({
   134→      metric: 'stance_width_norm',
   135→      deviation: (metrics.stance_width_norm - 0.5) * 100,
   136→    });
   137→  }
   138→
   139→  // Check rotation (ideal: 0.1-0.3)
   140→  if (metrics.shoulder_hip_rotation_proxy < 0.1) {
   141→    issues.push({
   142→      metric: 'shoulder_hip_rotation_proxy',
   143→      deviation: (0.1 - metrics.shoulder_hip_rotation_proxy) * 100,
   144→    });
   145→  } else if (metrics.shoulder_hip_rotation_proxy > 0.3) {
   146→    issues.push({
   147→      metric: 'shoulder_hip_rotation_proxy',
   148→      deviation: (metrics.shoulder_hip_rotation_proxy - 0.3) * 100,
   149→    });
   150→  }
   151→
   152→  // Return the metric with the largest deviation, or a default
   153→  if (issues.length === 0) {
   154→    return 'general_form';
   155→  }
   156→
   157→  issues.sort((a, b) => b.deviation - a.deviation);
   158→  return issues[0].metric;
   159→}
