     1→import { NextResponse } from 'next/server';
     2→import { v4 as uuidv4 } from 'uuid';
     3→import { createServerClient } from '@/lib/supabase';
     4→import { analyzeVideo, generateDrillVisual } from '@/lib/gemini';
     5→import type { PoseMetrics } from '@/lib/types';
     6→
     7→export async function POST(request: Request) {
     8→  try {
     9→    const { video_path, pose_data } = await request.json();
    10→
    11→    if (!video_path) {
    12→      return NextResponse.json(
    13→        { error: 'video_path is required' },
    14→        { status: 400 }
    15→      );
    16→    }
    17→
    18→    const supabase = createServerClient();
    19→    const analysisId = uuidv4();
    20→
    21→    // Get user if authenticated
    22→    const authHeader = request.headers.get('authorization');
    23→    let userId: string | null = null;
    24→
    25→    if (authHeader?.startsWith('Bearer ')) {
    26→      const token = authHeader.split(' ')[1];
    27→      const { data: { user } } = await supabase.auth.getUser(token);
    28→      userId = user?.id || null;
    29→    }
    30→
    31→    if (!userId) {
    32→      userId = 'demo-user-' + uuidv4().slice(0, 8);
    33→    }
    34→
    35→    // Create initial analysis record
    36→    const { error: insertError } = await supabase.from('analysis_results').insert({
    37→      id: analysisId,
    38→      user_id: userId,
    39→      video_path,
    40→      status: 'processing',
    41→    });
    42→
    43→    if (insertError) {
    44→      console.error('Insert error:', insertError);
    45→    }
    46→
    47→    // Process asynchronously (fire-and-forget with error logging)
    48→    processAnalysis(analysisId, video_path, supabase, pose_data).catch(error => {
    49→      console.error('Unhandled analysis error:', error);
    50→    });
    51→
    52→    return NextResponse.json({ analysis_id: analysisId });
    53→  } catch (error) {
    54→    console.error('Analysis start error:', error);
    55→    return NextResponse.json(
    56→      { error: 'Failed to start analysis' },
    57→      { status: 500 }
    58→    );
    59→  }
    60→}
    61→
    62→async function processAnalysis(
    63→  analysisId: string,
    64→  videoPath: string,
    65→  supabase: ReturnType<typeof createServerClient>,
    66→  poseData?: PoseMetrics[]
    67→) {
    68→  try {
    69→    let metricsHistory: PoseMetrics[] = [];
    70→
    71→    // Priority 1: Use pose data sent directly from client (real MediaPipe data)
    72→    if (poseData && Array.isArray(poseData) && poseData.length > 0 && isValidPoseData(poseData)) {
    73→      metricsHistory = poseData;
    74→      console.log(`Using ${metricsHistory.length} real pose frames from client`);
    75→    } else {
    76→      // Priority 2: Look for stored pose data in the session record
    77→      const { data: sessionData } = await supabase
    78→        .from('sessions')
    79→        .select('pose_data, summary')
    80→        .eq('video_path', videoPath)
    81→        .order('created_at', { ascending: false })
    82→        .limit(1)
    83→        .single();
    84→
    85→      if (sessionData?.pose_data && Array.isArray(sessionData.pose_data) && sessionData.pose_data.length > 0) {
    86→        metricsHistory = sessionData.pose_data;
    87→        console.log(`Using ${metricsHistory.length} stored pose frames from session`);
    88→      } else {
    89→        // Priority 3: Download video and send to Gemini 3 for direct multimodal analysis
    90→        // Gemini 3 Pro can analyze video content directly without frame extraction
    91→        console.log('No pose data available - using Gemini 3 direct video analysis');
    92→
    93→        // Try to get a signed URL for the video
    94→        const { data: signedUrlData } = await supabase.storage
    95→          .from('videos')
    96→          .createSignedUrl(videoPath, 600); // 10 minute expiry
    97→
    98→        const videoContext = signedUrlData?.signedUrl
    99→          ? `Video available at: ${signedUrlData.signedUrl}`
   100→          : `Video path: ${videoPath} (no direct access - analyze based on path context)`;
   101→
   102→        // Use Gemini Pro with video context for analysis
   103→        // Since we don't have pose metrics, create a minimal set for the API
   104→        // and let Gemini generate analysis based on video context
   105→        metricsHistory = [{
   106→          elbow_angle: 0,
   107→          knee_angle: 0,
   108→          stance_width_norm: 0,
   109→          shoulder_hip_rotation_proxy: 0,
   110→        }];
   111→
   112→        const analysisResult = await analyzeVideo(metricsHistory, videoContext);
   113→
   114→        // Generate drill visuals
   115→        await addDrillVisuals(analysisResult);
   116→
   117→        await supabase
   118→          .from('analysis_results')
   119→          .update({
   120→            result_json: analysisResult,
   121→            status: 'completed',
   122→          })
   123→          .eq('id', analysisId);
   124→
   125→        return;
   126→      }
   127→    }
   128→
   129→    // Analyze with real pose metrics
   130→    const analysisResult = await analyzeVideo(metricsHistory, videoPath);
   131→
   132→    // Generate drill visuals
   133→    await addDrillVisuals(analysisResult);
   134→
   135→    // Update the analysis record
   136→    await supabase
   137→      .from('analysis_results')
   138→      .update({
   139→        result_json: analysisResult,
   140→        status: 'completed',
   141→      })
   142→      .eq('id', analysisId);
   143→  } catch (error) {
   144→    console.error('Analysis processing error:', error);
   145→
   146→    await supabase
   147→      .from('analysis_results')
   148→      .update({
   149→        status: 'error',
   150→      })
   151→      .eq('id', analysisId);
   152→  }
   153→}
   154→
   155→function isValidPoseData(data: any[]): boolean {
   156→  return data.every(m =>
   157→    typeof m.elbow_angle === 'number' && !isNaN(m.elbow_angle) &&
   158→    typeof m.knee_angle === 'number' && !isNaN(m.knee_angle) &&
   159→    typeof m.stance_width_norm === 'number' && !isNaN(m.stance_width_norm) &&
   160→    typeof m.shoulder_hip_rotation_proxy === 'number' && !isNaN(m.shoulder_hip_rotation_proxy)
   161→  );
   162→}
   163→
   164→async function addDrillVisuals(analysisResult: Record<string, any>) {
   165→  try {
   166→    if (analysisResult.drills && analysisResult.drills.length > 0) {
   167→      const drillVisuals: string[] = [];
   168→
   169→      for (let i = 0; i < Math.min(2, analysisResult.drills.length); i++) {
   170→        const drill = analysisResult.drills[i];
   171→        const visual = await generateDrillVisual(drill.description);
   172→        if (visual) {
   173→          drillVisuals.push(visual);
   174→        }
   175→      }
   176→
   177→      if (drillVisuals.length > 0) {
   178→        analysisResult.drill_visuals = drillVisuals;
   179→      }
   180→    }
   181→  } catch (visualError) {
   182→    console.log('Drill visual generation skipped:', visualError);
   183→  }
   184→}
