     8→export async function POST(request: Request) {
     9→  try {
    10→    const body: RacketRecommendRequest = await request.json();
    11→    const { level, weaknesses, style_pref } = body;
    12→
    13→    if (!level || !weaknesses || weaknesses.length === 0) {
