   108→
   109→/**
   110→ * Analyze rally context to understand patterns and weaknesses
   111→ */
   112→function analyzeRallyContext(
   113→  shots: ShotData[] = [],
   114→  opponentPositions: PositionData[] = []
   115→): StrategyRecommendResponse['context'] {
   116→  const tags: string[] = [];
   117→  const opponentWeaknesses: string[] = [];
   118→  let notes = '';
   119→
   120→  // Analyze shot patterns
   121→  if (shots.length > 0) {
   122→    const shotTypes = shots.map(s => s.type);
   123→    const clearCount = shotTypes.filter(t => t === 'clear').length;
   124→    const dropCount = shotTypes.filter(t => t === 'drop').length;
   125→    const smashCount = shotTypes.filter(t => t === 'smash').length;
   126→    const netCount = shotTypes.filter(t => t === 'net').length;
   127→
   128→    // Tag the rally style
   129→    if (clearCount > shots.length * 0.4) {
   130→      tags.push('defensive-rally');
   131→    }
   132→    if (smashCount > 2) {
   133→      tags.push('aggressive-rally');
   134→    }
   135→    if (netCount > shots.length * 0.3) {
   136→      tags.push('net-battle');
   137→    }
   138→    if (dropCount > shots.length * 0.3) {
   139→      tags.push('drop-focused');
   140→    }
   141→
   142→    // Analyze end positions for weaknesses
   143→    const endPositions = shots.map(s => ({ x: s.endX, y: s.endY }));
   144→    const avgEndX = endPositions.reduce((sum, p) => sum + p.x, 0) / endPositions.length;
   145→    const avgEndY = endPositions.reduce((sum, p) => sum + p.y, 0) / endPositions.length;
   146→
   147→    if (avgEndX < 0.4) {
   148→      opponentWeaknesses.push('forehand-corner');
   149→      notes += 'Opponent appears weaker on forehand side. ';
   150→    }
   151→    if (avgEndX > 0.6) {
   152→      opponentWeaknesses.push('backhand-corner');
   153→      notes += 'Opponent appears weaker on backhand side. ';
   154→    }
   155→    if (avgEndY > 0.7) {
   156→      opponentWeaknesses.push('rear-court');
   157→      notes += 'Opponent struggles with deep shots. ';
   158→    }
   159→    if (avgEndY < 0.3) {
   160→      opponentWeaknesses.push('front-court');
   161→      notes += 'Opponent late to front court. ';
   162→    }
   163→  }
   164→
   165→  // Default context if no shots
   166→  if (tags.length === 0) {
   167→    tags.push('general-rally');
   168→  }
   169→  if (opponentWeaknesses.length === 0) {
   170→    opponentWeaknesses.push('unknown');
   171→  }
   172→  if (!notes) {
   173→    notes = 'Insufficient data for detailed analysis. General recommendations provided.';
   174→  }
   175→
   176→  return { tags, opponentWeaknesses, notes };
   177→}
   178→
   179→/**
   180→ * Generate strategic recommendations based on rally context
   181→ */
   182→function generateRecommendations(
   183→  shots: ShotData[] = [],
   184→  context: StrategyRecommendResponse['context'],
   185→  matchContext?: StrategyRecommendRequest['matchContext']
   186→): StrategyRecommendation[] {
   187→  const recommendations: StrategyRecommendation[] = [];
   188→
   189→  // Recommendation 1: Deep Clear Strategy
   190→  // Best when opponent has rear-court weakness
   191→  const deepClearConfidence = context.opponentWeaknesses.includes('rear-court') ? 92 : 75;
   192→  recommendations.push({
   193→    id: 'deep-clear',
   194→    name: 'Deep Clear Strategy',
   195→    reason: context.opponentWeaknesses.includes('rear-court')
   196→      ? 'Exploit opponent\'s weakness at the rear court with consistent deep clears to force weak returns.'
   197→      : 'Push opponent to back court to create attacking opportunities from their weak lifts.',
   198→    confidence: deepClearConfidence,
   199→    jumpToVideoTimeSec: 2,
   200→    trajectory2D: generateDeepClearTrajectory(),
   201→    trajectory3D: generateDeepClearTrajectory3D(),
   202→  });
   203→
   204→  // Recommendation 2: Cross-Court Attack
   205→  // Best when opponent has side weakness
   206→  const crossCourtConfidence =
   207→    context.opponentWeaknesses.includes('forehand-corner') ||
   208→    context.opponentWeaknesses.includes('backhand-corner') ? 88 : 70;
   209→  const targetSide = context.opponentWeaknesses.includes('backhand-corner') ? 'backhand' : 'forehand';
   210→  recommendations.push({
   211→    id: 'cross-court',
   212→    name: 'Cross-Court Attack',
   213→    reason: `Use sharp cross-court angles to target opponent's ${targetSide} side and create straight-line kill opportunities.`,
   214→    confidence: crossCourtConfidence,
   215→    jumpToVideoTimeSec: 4,
   216→    trajectory2D: generateCrossCourtTrajectory(targetSide === 'backhand'),
   217→    trajectory3D: generateCrossCourtTrajectory3D(targetSide === 'backhand'),
   218→  });
   219→
   220→  // Recommendation 3: Net Domination
   221→  // Best when opponent has front-court weakness
   222→  const netDominationConfidence = context.opponentWeaknesses.includes('front-court') ? 90 : 72;
   223→  recommendations.push({
   224→    id: 'net-domination',
   225→    name: 'Net Domination',
   226→    reason: context.opponentWeaknesses.includes('front-court')
   227→      ? 'Opponent is slow to front court. Control the net with tight shots to force weak lifts.'
   228→      : 'Take control of the rally by dominating the net area with deceptive tumbling shots.',
   229→    confidence: netDominationConfidence,
   230→    jumpToVideoTimeSec: 6,
   231→    trajectory2D: generateNetDominationTrajectory(),
   232→    trajectory3D: generateNetDominationTrajectory3D(),
   233→  });
   234→
   235→  // Recommendation 4: Smash and Follow (aggressive, for match/game point)
   236→  if (matchContext?.gamePoint || matchContext?.matchPoint) {
   237→    recommendations.push({
   238→      id: 'smash-follow',
   239→      name: 'Smash and Follow',
   240→      reason: 'Critical point - apply maximum pressure with powerful smash followed by quick net cover.',
   241→      confidence: 85,
   242→      jumpToVideoTimeSec: 8,
   243→      trajectory2D: generateSmashFollowTrajectory(),
   244→      trajectory3D: generateSmashFollowTrajectory3D(),
   245→    });
   246→  }
   247→
   248→  // Sort by confidence and return top 3
   249→  return recommendations
   250→    .sort((a, b) => b.confidence - a.confidence)
   251→    .slice(0, 3);
   252→}
   253→
   254→// Trajectory generation helpers
   255→function generateDeepClearTrajectory(): TrajectoryPoint[] {
   256→  const points: TrajectoryPoint[] = [];
   257→  for (let i = 0; i < 8; i++) {
   258→    const isEven = i % 2 === 0;
   259→    points.push({
   260→      x: isEven ? 0.7 : 0.3,
   261→      y: isEven ? 0.1 : 0.9,
   262→      t: i * 2,
   263→    });
   264→  }
   265→  return points;
   266→}
   267→
   268→function generateDeepClearTrajectory3D(): TrajectoryPoint[] {
   269→  return generateDeepClearTrajectory().map(p => ({ ...p, z: 0 }));
   270→}
   271→
   272→function generateCrossCourtTrajectory(toBackhand: boolean): TrajectoryPoint[] {
   273→  const points: TrajectoryPoint[] = [];
   274→  for (let i = 0; i < 8; i++) {
   275→    const isEven = i % 2 === 0;
   276→    points.push({
   277→      x: isEven ? (toBackhand ? 0.8 : 0.2) : (toBackhand ? 0.2 : 0.8),
   278→      y: isEven ? 0.3 : 0.7,
   279→      t: i * 2,
   280→    });
   281→  }
   282→  return points;
   283→}
   284→
   285→function generateCrossCourtTrajectory3D(toBackhand: boolean): TrajectoryPoint[] {
   286→  return generateCrossCourtTrajectory(toBackhand).map(p => ({ ...p, z: 0 }));
   287→}
   288→
   289→function generateNetDominationTrajectory(): TrajectoryPoint[] {
   290→  const points: TrajectoryPoint[] = [];
   291→  for (let i = 0; i < 8; i++) {
   292→    const isEven = i % 2 === 0;
   293→    points.push({
   294→      x: 0.3 + Math.random() * 0.4,
   295→      y: isEven ? 0.25 : 0.55,
   296→      t: i * 1.5, // Faster exchanges
   297→    });
   298→  }
   299→  return points;
   300→}
   301→
   302→function generateNetDominationTrajectory3D(): TrajectoryPoint[] {
   303→  return generateNetDominationTrajectory().map(p => ({ ...p, z: 0 }));
   304→}
   305→
   306→function generateSmashFollowTrajectory(): TrajectoryPoint[] {
   307→  const points: TrajectoryPoint[] = [];
   308→  // Smash from rear
   309→  points.push({ x: 0.5, y: 0.15, t: 0 });
   310→  // Land steep at opponent
   311→  points.push({ x: 0.6, y: 0.75, t: 1 });
   312→  // Follow to net
   313→  points.push({ x: 0.5, y: 0.35, t: 2 });
   314→  // Net kill
   315→  points.push({ x: 0.4, y: 0.55, t: 3 });
   316→  // Winner
   317→  points.push({ x: 0.7, y: 0.8, t: 4 });
   318→  return points;
   319→}
   320→
   321→function generateSmashFollowTrajectory3D(): TrajectoryPoint[] {
   322→  return generateSmashFollowTrajectory().map(p => ({ ...p, z: 0 }));
   323→}
