    60→  for (const landmarks of poseData) {
    61→    if (!landmarks || landmarks.length === 0) {
    62→      dropoutCount++;
    63→      continue;
    64→    }
    65→
    66→    // Calculate average visibility for key joints in this frame
    67→    let frameVisibilitySum = 0;
    68→    let keyJointCount = 0;
