     3→// MediaPipe Pose landmark indices
     4→export const POSE_LANDMARKS = {
     5→  NOSE: 0,
     6→  LEFT_EYE_INNER: 1,
     7→  LEFT_EYE: 2,
     8→  LEFT_EYE_OUTER: 3,
     9→  RIGHT_EYE_INNER: 4,
    10→  RIGHT_EYE: 5,
    11→  RIGHT_EYE_OUTER: 6,
    12→  LEFT_EAR: 7,
    13→  RIGHT_EAR: 8,
    14→  MOUTH_LEFT: 9,
    15→  MOUTH_RIGHT: 10,
    16→  LEFT_SHOULDER: 11,
    17→  RIGHT_SHOULDER: 12,
    18→  LEFT_ELBOW: 13,
    19→  RIGHT_ELBOW: 14,
    20→  LEFT_WRIST: 15,
    21→  RIGHT_WRIST: 16,
    22→  LEFT_PINKY: 17,
    23→  RIGHT_PINKY: 18,
    24→  LEFT_INDEX: 19,
    25→  RIGHT_INDEX: 20,
    26→  LEFT_THUMB: 21,
    27→  RIGHT_THUMB: 22,
    28→  LEFT_HIP: 23,
    29→  RIGHT_HIP: 24,
    30→  LEFT_KNEE: 25,
    31→  RIGHT_KNEE: 26,
    32→  LEFT_ANKLE: 27,
    33→  RIGHT_ANKLE: 28,
    34→  LEFT_HEEL: 29,
    35→  RIGHT_HEEL: 30,
    36→  LEFT_FOOT_INDEX: 31,
    37→  RIGHT_FOOT_INDEX: 32,
