    37// Schema for practice tick response
    38const tickResponseSchema = {
    39  type: Type.OBJECT,
    40  properties: {
    41    cue: { type: Type.STRING, description: 'Short coaching cue (max 10 words)' },
    42    focus_metric: { type: Type.STRING, description: 'The metric to focus on' },
    43    is_green: { type: Type.BOOLEAN, description: 'Whether current form is good' },
    44    commentary: { type: Type.STRING, description: 'Real-time multimodal commentary on the player movement (1-2 sentences, conversational and encouraging)' },
    45    reason: { type: Type.STRING, description: 'Brief reasoning for why this cue was chosen based on the metrics (1 sentence)' },
    46  },
    47  required: ['cue', 'focus_metric', 'is_green', 'commentary', 'reason'],
    48};
    49
    50// Schema for analysis result
    51const analysisResultSchema = {
    52  type: Type.OBJECT,
    53  properties: {
    54    top_issues: {
    55      type: Type.ARRAY,
