   102// Gemini Flash: Live micro-coaching for practice mode
   103export async function getCoachingCue(
   104  metrics: PoseMetrics,
   105  drillContext?: string
   106): Promise<{ cue: string; focus_metric: string; is_green: boolean; commentary: string; reason: string; latency_ms: number }> {
   107  const prompt = `You are an expert badminton coach providing real-time live commentary during practice.
   108
   109Current player pose metrics (from live camera pose estimation):
   110- Elbow angle: ${metrics.elbow_angle.toFixed(1)} (ideal: 90-120 for clear/smash shots)
   111- Knee angle: ${metrics.knee_angle.toFixed(1)} (ideal: 120-150 for ready position)
   112- Stance width (normalized): ${metrics.stance_width_norm.toFixed(2)} (ideal: 0.3-0.5)
   113- Shoulder-hip rotation: ${metrics.shoulder_hip_rotation_proxy.toFixed(2)} (ideal: 0.1-0.3)
   114
   115${drillContext ? `Current drill: ${drillContext}` : ''}
   116
   117Provide:
   1181. ONE short coaching cue (max 10 words) - the most important correction right now
   1192. The metric that needs most focus
   1203. Whether overall form is acceptable (is_green)
   1214. A brief real-time commentary (1-2 sentences) as if you were watching the player live - be conversational and encouraging
   1225. A reasoning hint explaining WHY you chose this specific cue based on the metrics`;
   123
   124  const startTime = Date.now();
   125
   126  try {
   127    const response = await genAI.models.generateContent({
   128      model: FLASH_MODEL,
   129      contents: prompt,
   130      config: {
   131        responseMimeType: 'application/json',
   132        responseSchema: tickResponseSchema,
   133        temperature: 0.3,
   134        maxOutputTokens: 2000,
   135        thinkingConfig: {
   136          thinkingBudget: 200,
   137        },
   138      },
   139    });
   140
   141    const latency_ms = Date.now() - startTime;
   142    const result = safeParseJSON(response.text || '{}');
   143    return {
   144      cue: result.cue || 'Keep your form steady',
   145      focus_metric: result.focus_metric || 'elbow_angle',
   146      is_green: result.is_green ?? false,
   147      commentary: result.commentary || '',
   148      reason: result.reason || '',
   149      latency_ms,
   150    };
   151  } catch (error) {
   152    console.error('Gemini Flash error:', error);
   153    const latency_ms = Date.now() - startTime;
   154    return {
   155      cue: 'Stay focused on your stance',
   156      focus_metric: 'stance_width_norm',
   157      is_green: metrics.knee_angle >= 120 && metrics.knee_angle <= 150,
   158      commentary: '',
   159      reason: '',
   160      latency_ms,
   161    };
   162  }
   163}
