   165// Gemini Pro: Detailed video analysis
   166export async function analyzeVideo(
   167  metricsHistory: PoseMetrics[],
   168  videoContext?: string
   169): Promise<AnalysisResultJson> {
   170  // Calculate average metrics
   171  const avgMetrics = metricsHistory.reduce(
   172    (acc, m) => ({
