     9// Error boundary to catch R3F errors during HMR
    10interface CanvasErrorBoundaryProps {
    11  children: ReactNode;
    12  fallback?: ReactNode;
    13}
    14
    15interface CanvasErrorBoundaryState {
    16  hasError: boolean;
    17  errorKey: number;
    18}
    19
    20class CanvasErrorBoundary extends Component<CanvasErrorBoundaryProps, CanvasErrorBoundaryState> {
    21  constructor(props: CanvasErrorBoundaryProps) {
    22    super(props);
    23    this.state = { hasError: false, errorKey: 0 };
    24  }
    25
    26  static getDerivedStateFromError(): Partial<CanvasErrorBoundaryState> {
    27    return { hasError: true };
    28  }
    29
    30  componentDidCatch(error: Error) {
    31    // Log but don't crash - this handles R3F HMR errors
    32    console.warn('Canvas error caught (likely HMR):', error.message);
    33  }
    34
    35  componentDidUpdate(_prevProps: CanvasErrorBoundaryProps, prevState: CanvasErrorBoundaryState) {
    36    if (this.state.hasError && !prevState.hasError) {
    37      // Auto-recover after a brief delay
    38      setTimeout(() => {
    39        this.setState({ hasError: false, errorKey: this.state.errorKey + 1 });
    40      }, 100);
    41    }
    42  }
    43
    44  render() {
    45    if (this.state.hasError) {
    46      return this.props.fallback || (
    47        <div className="h-full flex items-center justify-center text-slate-400">
    48          <span className="text-sm">Reloading 3D view...</span>
    49        </div>
    50      );
    51    }
    52    return <div key={this.state.errorKey} className="h-full">{this.props.children}</div>;
    53  }
    54}
