   162        onPointerOver={() => setHovered(true)}
   163        onPointerOut={() => setHovered(false)}
   164      >
   165        <sphereGeometry args={[size, 16, 16]} />
   166        <meshStandardMaterial
   167          color={color}
   168          emissive={highlighted ? color : '#000000'}
   169          emissiveIntensity={highlighted ? 0.5 : 0}
