   212// Convert MediaPipe landmarks to 3D positions
   213function landmarksTo3D(
   214  landmarks: PoseLandmark[],
   215  scale: number = 2
   216): Map<number, [number, number, number]> {
   217  const positions = new Map<number, [number, number, number]>();
   218
   219  for (let i = 0; i < landmarks.length; i++) {
   220    const lm = landmarks[i];
   221    if (lm && lm.visibility > 0.05) {
   222      // Center and scale the pose
   223      const x = (lm.x - 0.5) * scale;
   224      const y = -(lm.y - 0.5) * scale; // Flip Y for 3D
   225      const z = -lm.z * scale;
   226      positions.set(i, [x, y, z]);
   227    }
   228  }
   229
   230  return positions;
   231}
