   233interface SkeletonMeshProps {
   234  landmarks: PoseLandmark[];
   235  color: string;
   236  highlightedJoints: number[];
   237  deviations?: JointDeviation[];
   238  opacity?: number;
   239}
   240
   241function SkeletonMesh({ landmarks, color, highlightedJoints, deviations = [], opacity = 1 }: SkeletonMeshProps) {
   242  const positions = useMemo(() => landmarksTo3D(landmarks), [landmarks]);
   243
   244  const joints = useMemo(() => {
   245    const result: JSX.Element[] = [];
   246
   247    positions.forEach((pos, idx) => {
   248      const isHighlighted = highlightedJoints.includes(idx);
   249      const deviation = deviations.find((d) => d.jointIndex === idx);
   250
   251      let jointColor = color;
   252      if (deviation) {
   253        if (deviation.severity === 'error') jointColor = '#ef4444';
   254        else if (deviation.severity === 'warning') jointColor = '#eab308';
   255        else jointColor = '#22c55e';
   256      }
   257
   258      result.push(
   259        <Joint
   260          key={idx}
   261          position={pos}
   262          color={jointColor}
   263          size={0.04}
   264          highlighted={isHighlighted || deviation?.severity === 'error'}
   265          name={JOINT_NAMES[idx]}
   266          deviation={deviation}
   267        />
   268      );
   269    });
   270
   271    return result;
   272  }, [positions, color, highlightedJoints, deviations]);
   273
   274  const bones = useMemo(() => {
   275    const result: JSX.Element[] = [];
   276
   277    for (const [startIdx, endIdx] of POSE_CONNECTIONS) {
   278      const start = positions.get(startIdx);
   279      const end = positions.get(endIdx);
   280
   281      if (start && end) {
   282        const startDev = deviations.find((d) => d.jointIndex === startIdx);
   283        const endDev = deviations.find((d) => d.jointIndex === endIdx);
   284
   285        let boneColor = color;
   286        if (startDev?.severity === 'error' || endDev?.severity === 'error') {
   287          boneColor = '#ef4444';
   288        } else if (startDev?.severity === 'warning' || endDev?.severity === 'warning') {
   289          boneColor = '#eab308';
   290        }
   291
   292        result.push(
   293          <Bone
   294            key={`${startIdx}-${endIdx}`}
   295            start={start}
   296            end={end}
   297            color={boneColor}
   298            thickness={3}
   299          />
   300        );
   301      }
   302    }
   303
   304    return result;
   305  }, [positions, color, deviations]);
   306
   307  return (
   308    <group>
   309      {bones}
   310      {joints}
   311    </group>
   312  );
   313}
