   321function CorrectionArrow({ from, to }: CorrectionArrowProps) {
   322  const direction = useMemo(() => {
   323    const dir = new THREE.Vector3(to[0] - from[0], to[1] - from[1], to[2] - from[2]);
   324    return dir.normalize();
