    43// Pseudo-3D height curve based on shot type heuristic
    44function calculateHeight(
    45  startY: number,
    46  endY: number,
    47  t: number,
    48  shotIndex: number
    49): number {
    50  // Determine shot type based on court position change
    51  const deltaY = Math.abs(endY - startY);
    52
    53  // Higher arc for clears (long shots), lower for drives and drops
    54  let maxHeight = 1.5; // Default medium height
    55  if (deltaY > 0.5) {
    56    // Long shot - clear
    57    maxHeight = 3.5;
    58  } else if (deltaY < 0.2) {
    59    // Short shot - drop or net
    60    maxHeight = 1.0;
    61  }
    62
    63  // Parabolic arc with some variation
    64  const arc = Math.sin(t * Math.PI);
    65  return arc * maxHeight + 0.3; // Minimum height above net
    66}
