    68// Ball component that follows trajectory - uses refs for smooth animation
    69function Ball({
    70  trajectory,
    71  progress,
    72  color,
    73  show,
    74}: {
    75  trajectory: TrajectoryPoint[];
    76  progress: number;
    77  color: string;
    78  show: boolean;
    79}) {
    80  const meshRef = useRef<THREE.Mesh>(null);
    81  const progressRef = useRef(progress);
    82
    83  // Update progress ref
    84  useEffect(() => {
    85    progressRef.current = progress;
    86  }, [progress]);
    87
    88  useFrame(() => {
    89    if (!meshRef.current || !show || trajectory.length < 2) return;
    90
    91    const currentProgress = progressRef.current;
    92
    93    // Calculate current position along trajectory
    94    const totalPoints = trajectory.length;
    95    const currentIndex = Math.min(
    96      Math.floor(currentProgress * totalPoints),
    97      totalPoints - 1
    98    );
    99    const nextIndex = Math.min(currentIndex + 1, totalPoints - 1);
   100    const localT = (currentProgress * totalPoints) % 1;
   101
   102    const p1 = trajectory[currentIndex];
   103    const p2 = trajectory[nextIndex];
   104
   105    // Convert normalized coordinates to 3D scene coordinates
   106    const pos1 = normalizedTo3D(p1.x, p1.y);
   107    const pos2 = normalizedTo3D(p2.x, p2.y);
   108
   109    // Interpolate position
   110    const x = THREE.MathUtils.lerp(pos1.x, pos2.x, localT);
   111    const z = THREE.MathUtils.lerp(pos1.z, pos2.z, localT);
   112    const y = calculateHeight(p1.y, p2.y, localT, currentIndex);
   113
   114    meshRef.current.position.set(x, y, z);
   115  });
   116
   117  if (!show) return null;
