   127// Trajectory path visualization - uses memoization for performance
   128function TrajectoryPath({
   129  trajectory,
   130  progress,
   131  color,
   132  show,
   133}: {
   134  trajectory: TrajectoryPoint[];
   135  progress: number;
   136  color: string;
   137  show: boolean;
   138}) {
   139  const points = useMemo(() => {
   140    if (!show || trajectory.length < 2) return [];
   141
   142    const result: THREE.Vector3[] = [];
   143    const visiblePoints = Math.ceil(progress * trajectory.length);
   144
   145    for (let i = 0; i < Math.min(visiblePoints, trajectory.length); i++) {
   146      const p = trajectory[i];
   147      const nextP = trajectory[Math.min(i + 1, trajectory.length - 1)];
   148
   149      // Convert normalized coordinates to 3D scene coordinates
   150      const pos = normalizedTo3D(p.x, p.y);
   151      const nextPos = normalizedTo3D(nextP.x, nextP.y);
   152
   153      // Add multiple points along each segment for smooth curve
   154      for (let t = 0; t < 1; t += 0.1) {
   155        const x = THREE.MathUtils.lerp(pos.x, nextPos.x, t);
   156        const z = THREE.MathUtils.lerp(pos.z, nextPos.z, t);
   157        const y = calculateHeight(p.y, nextP.y, t, i);
   158        result.push(new THREE.Vector3(x, y, z));
   159      }
   160    }
   161
   162    return result;
   163  }, [trajectory, progress, show]);
   164
   165  if (!show || points.length < 2) return null;
   166
   167  return (
   168    <Line
   169      points={points}
   170      color={color}
   171      lineWidth={3}
   172      dashed
   173      dashSize={0.3}
   174      gapSize={0.1}
   175    />
   176  );
   177}
