   179// Court plane with lines - BWF standard badminton court
   180function Court() {
   181  // Helper to convert court meters (0,0 at bottom-left) to 3D scene coords (centered)
   182  const toScene = (meterX: number, meterY: number): [number, number, number] => {
   183    return [
   184      meterX - COURT_WIDTH / 2,  // X: centered
   185      0,                          // Y: at ground level
   186      meterY - COURT_LENGTH / 2, // Z: centered (Y meter = Z scene)
