   320// Animation controller component - handles time updates using refs to avoid stale closures
   321function AnimationController({
   322  isPlaying,
   323  timeScale,
   324  onProgressUpdate,
   325  initialProgress,
   326}: {
   327  isPlaying: boolean;
   328  timeScale: number;
   329  onProgressUpdate: (progress: number) => void;
   330  initialProgress: number;
   331}) {
   332  const progressRef = useRef(initialProgress);
   333  const isPlayingRef = useRef(isPlaying);
   334  const timeScaleRef = useRef(timeScale);
   335
   336  // Keep refs in sync with props
   337  useEffect(() => {
   338    isPlayingRef.current = isPlaying;
   339  }, [isPlaying]);
   340
   341  useEffect(() => {
   342    timeScaleRef.current = timeScale;
   343  }, [timeScale]);
   344
   345  useEffect(() => {
   346    progressRef.current = initialProgress;
   347  }, [initialProgress]);
   348
   349  useFrame((state, delta) => {
   350    if (!isPlayingRef.current) return;
   351
   352    // Update progress based on delta time and timeScale
   353    // Full loop is 10 seconds at 1x speed
   354    const increment = (delta * timeScaleRef.current) / 10;
   355    progressRef.current += increment;
   356
   357    // Loop back to start
   358    if (progressRef.current >= 1) {
   359      progressRef.current = 0;
   360    }
   361
   362    // Notify parent (throttled to avoid excessive updates)
   363    onProgressUpdate(progressRef.current);
   364  });
   365
   366  return null;
   367}
