   369// Main 3D Scene
   370function Scene({
   371  originalTrajectory,
   372  refinedTrajectory,
   373  showOriginal,
   374  showRefined,
   375  animationProgress,
   376  isPlaying,
   377  timeScale = 1,
   378  allRecommendations,
   379  selectedRecommendationIndex = -1,
   380}: CourtTrajectory3DProps) {
   381  // Local progress state for smooth animation
   382  const [localProgress, setLocalProgress] = useState(animationProgress);
   383
   384  // Sync with external progress when it changes significantly
   385  useEffect(() => {
   386    const diff = Math.abs(localProgress - animationProgress);
   387    if (diff > 0.1 || !isPlaying) {
   388      setLocalProgress(animationProgress);
   389    }
   390  }, [animationProgress, isPlaying, localProgress]);
   391
   392  // Determine if we should render multiple paths
   393  const hasMultiplePaths = allRecommendations && allRecommendations.length > 0;
   394
   395  return (
   396    <>
   397      {/* Animation Controller - manages time updates without causing re-renders */}
   398      <AnimationController
   399        isPlaying={isPlaying}
   400        timeScale={timeScale}
   401        onProgressUpdate={setLocalProgress}
   402        initialProgress={animationProgress}
   403      />
   404
   405      {/* Lighting */}
   406      <ambientLight intensity={0.6} />
   407      <directionalLight position={[10, 10, 5]} intensity={0.8} />
   408      <directionalLight position={[-10, 10, -5]} intensity={0.4} />
   409
   410      {/* Court */}
   411      <Court />
   412
   413      {/* Net */}
   414      <Net />
   415
   416      {/* Original Trajectory (always shown in red if enabled) */}
   417      <TrajectoryPath
   418        trajectory={originalTrajectory}
   419        progress={localProgress}
   420        color="#ef4444"
   421        show={showOriginal}
   422      />
   423
   424      {/* Multiple Recommendation Paths */}
   425      {hasMultiplePaths ? (
   426        allRecommendations.map((rec, index) => {
   427          const lineStyle = RECOMMENDATION_LINE_STYLES[index] || RECOMMENDATION_LINE_STYLES[0];
   428          const isSelected = index === selectedRecommendationIndex;
   429
   430          return (
   431            <TrajectoryPath
   432              key={rec.id}
   433              trajectory={rec.trajectory2D}
   434              progress={localProgress}
   435              color={lineStyle.color}
   436              show={showRefined}
   437            />
   438          );
   439        })
   440      ) : (
   441        // Fallback to single refined trajectory
   442        <TrajectoryPath
   443          trajectory={refinedTrajectory}
   444          progress={localProgress}
   445          color="#22c55e"
   446          show={showRefined}
   447        />
   448      )}
   449
   450      {/* Balls */}
   451      <Ball
   452        trajectory={originalTrajectory}
   453        progress={localProgress}
   454        color="#ef4444"
   455        show={showOriginal}
   456      />
   457
   458      {/* Ball for selected recommendation or fallback */}
   459      {hasMultiplePaths && selectedRecommendationIndex >= 0 ? (
   460        <Ball
   461          trajectory={allRecommendations[selectedRecommendationIndex]?.trajectory2D || []}
   462          progress={localProgress}
   463          color={RECOMMENDATION_LINE_STYLES[selectedRecommendationIndex]?.color || '#22c55e'}
   464          show={showRefined && allRecommendations[selectedRecommendationIndex]?.trajectory2D?.length > 0}
   465        />
   466      ) : (
   467        <Ball
   468          trajectory={refinedTrajectory}
   469          progress={localProgress}
   470          color="#22c55e"
   471          show={showRefined}
   472        />
   473      )}
   474
   475      {/* Camera controls */}
   476      <OrbitControls
   477        enablePan={true}
   478        enableZoom={true}
   479        enableRotate={true}
   480        minDistance={5}
   481        maxDistance={25}
   482        target={[0, 1, 0]}
   483      />
   484    </>
   485  );
   486}
