   488// Error boundary wrapper to prevent blank screens
   489function ErrorFallback() {
   490  return (
   491    <div className="w-full h-[500px] bg-gray-900 rounded-lg flex items-center justify-center">
   492      <div className="text-center text-white">
   493        <p className="text-lg font-medium mb-2">3D View Unavailable</p>
   494        <p className="text-sm text-gray-400">Please try switching to 2D view</p>
   495      </div>
   496    </div>
   497  );
   498}
