   500// Export the main component with error handling
   501export default function CourtTrajectory3D(props: CourtTrajectory3DProps) {
   502  const [hasError, setHasError] = useState(false);
   503
   504  // Reset error state when props change
   505  useEffect(() => {
   506    setHasError(false);
   507  }, [props.showOriginal, props.showRefined]);
   508
   509  if (hasError) {
   510    return <ErrorFallback />;
   511  }
   512
   513  return (
   514    <div className="w-full h-[500px] bg-gray-900 rounded-lg overflow-hidden">
   515      <Canvas
   516        camera={{
   517          position: [0, 10, 12],
   518          fov: 50,
   519          near: 0.1,
   520          far: 100,
   521        }}
   522        onCreated={(state) => {
   523          // Ensure proper WebGL context handling
   524          state.gl.setPixelRatio(Math.min(window.devicePixelRatio, 2));
   525        }}
   526        onError={() => setHasError(true)}
   527      >
   528        <Scene {...props} />
   529      </Canvas>
   530    </div>
   531  );
   532}
