     1→'use client';
     2→
     3→import { useState, useEffect, useRef, useCallback, Suspense } from 'react';
     4→import { useSearchParams } from 'next/navigation';
     5→import DashboardNav from '@/components/DashboardNav';
     6→import { supabase, uploadPracticeRecording } from '@/lib/supabase';
     7→import {
     8→  extractMetrics,
     9→  drawSkeleton,
    10→  generateMockMetrics,
    11→  evaluateMetrics,
    12→  DEFAULT_THRESHOLDS,
    13→} from '@/lib/pose-utils';
    14→import {
    15→  evaluateForm,
    16→  DRILLS,
    17→  FORM_RULES,
    18→  type EvaluationResult,
    19→} from '@/lib/rules-engine';
    20→import type { PoseMetrics, PoseLandmark, TickResponse } from '@/lib/types';
    21→
    22→// MediaPipe Tasks Vision types
    23→type PoseLandmarker = any;
    24→type FilesetResolver = any;
    25→
    26→function PracticeContent() {
    27→  const searchParams = useSearchParams();
    28→  const drillId = searchParams.get('drill');
    29→
    30→  const videoRef = useRef<HTMLVideoElement>(null);
    31→  const canvasRef = useRef<HTMLCanvasElement>(null);
    32→  const poseLandmarkerRef = useRef<PoseLandmarker | null>(null);
    33→  const animationRef = useRef<number | null>(null);
    34→  const tickIntervalRef = useRef<NodeJS.Timeout | null>(null);
    35→  const sessionIdRef = useRef<string | null>(null);
    36→  const lastVideoTimeRef = useRef<number>(-1);
    37→  // PHASE 1: Track last MediaPipe timestamp to ensure strictly monotonic
    38→  const lastTimestampMsRef = useRef<number>(-1);
    39→  const currentLandmarksRef = useRef<PoseLandmark[] | null>(null);
    40→  // Use ref to avoid stale closure in requestAnimationFrame
    41→  const isActiveRef = useRef(false);
    42→  const showSkeletonRef = useRef(true);
    43→  const useMockDataRef = useRef(false);
    44→
    45→  // MediaRecorder refs for session recording
    46→  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
    47→  const recordedChunksRef = useRef<Blob[]>([]);
    48→  const streamRef = useRef<MediaStream | null>(null);
    49→
    50→  const [isActive, setIsActive] = useState(false);
    51→  const [isPoseLoaded, setPoseLoaded] = useState(false);
    52→  const [poseError, setPoseError] = useState<string | null>(null);
    53→  const [useMockData, setUseMockData] = useState(false);
    54→  const [showSkeleton, setShowSkeleton] = useState(true);
    55→
    56→  const [currentMetrics, setCurrentMetrics] = useState<PoseMetrics | null>(null);
    57→  const [currentEvaluation, setCurrentEvaluation] = useState<EvaluationResult | null>(null);
    58→  const [isGreen, setIsGreen] = useState(true);
    59→  const [coachingCue, setCoachingCue] = useState('Get ready to start!');
    60→  const [focusMetric, setFocusMetric] = useState('');
    61→  const [failingRules, setFailingRules] = useState<string[]>([]);
    62→
    63→  const [greenCount, setGreenCount] = useState(0);
    64→  const [redCount, setRedCount] = useState(0);
    65→  const [sessionStartTime, setSessionStartTime] = useState<Date | null>(null);
    66→
    67→  const [thresholds, setThresholds] = useState(DEFAULT_THRESHOLDS);
    68→
    69→  // Recording state
    70→  const [isRecording, setIsRecording] = useState(false);
    71→  const [recordingEnabled, setRecordingEnabled] = useState(true);
    72→  const [isUploading, setIsUploading] = useState(false);
    73→  const [uploadProgress, setUploadProgress] = useState(0);
    74→  const [uploadError, setUploadError] = useState<string | null>(null);
    75→
    76→  // Get drill info if a drill is selected
    77→  const currentDrill = drillId ? DRILLS[drillId] : null;
    78→
    79→  // Keep refs in sync with state to avoid stale closures in requestAnimationFrame
    80→  useEffect(() => {
    81→    isActiveRef.current = isActive;
    82→  }, [isActive]);
    83→
    84→  useEffect(() => {
    85→    showSkeletonRef.current = showSkeleton;
    86→  }, [showSkeleton]);
    87→
    88→  useEffect(() => {
    89→    useMockDataRef.current = useMockData;
    90→  }, [useMockData]);
    91→
    92→  // Initialize MediaPipe Pose Landmarker
    93→  useEffect(() => {
    94→    initializePose();
    95→    return () => {
    96→      cleanup();
    97→    };
    98→  }, []);
    99→
   100→  const initializePose = async () => {
   101→    try {
   102→      // Dynamically import MediaPipe Tasks Vision
   103→      const vision = await import('@mediapipe/tasks-vision');
   104→      const { PoseLandmarker, FilesetResolver } = vision;
   105→
   106→      // Load the vision WASM files
   107→      const filesetResolver = await FilesetResolver.forVisionTasks(
   108→        'https://cdn.jsdelivr.net/npm/@mediapipe/tasks-vision@latest/wasm'
   109→      );
   110→
   111→      // Create the pose landmarker
   112→      poseLandmarkerRef.current = await PoseLandmarker.createFromOptions(filesetResolver, {
   113→        baseOptions: {
   114→          modelAssetPath: 'https://storage.googleapis.com/mediapipe-models/pose_landmarker/pose_landmarker_lite/float16/1/pose_landmarker_lite.task',
   115→          delegate: 'GPU',
   116→        },
   117→        runningMode: 'VIDEO',
   118→        numPoses: 1,
   119→        minPoseDetectionConfidence: 0.5,
   120→        minPosePresenceConfidence: 0.5,
   121→        minTrackingConfidence: 0.5,
   122→      });
   123→
   124→      setPoseLoaded(true);
   125→    } catch (error) {
   126→      console.error('Failed to load MediaPipe Pose:', error);
   127→      setPoseError('Pose detection failed to load. Demo mode enabled.');
   128→      useMockDataRef.current = true;
   129→      setUseMockData(true);
   130→      setPoseLoaded(true);
   131→    }
   132→  };
   133→
   134→  const cleanup = () => {
   135→    isActiveRef.current = false;
   136→    if (animationRef.current) {
   137→      cancelAnimationFrame(animationRef.current);
   138→      animationRef.current = null;
   139→    }
   140→    if (tickIntervalRef.current) {
   141→      clearInterval(tickIntervalRef.current);
   142→      tickIntervalRef.current = null;
   143→    }
   144→    if (poseLandmarkerRef.current) {
   145→      poseLandmarkerRef.current.close();
   146→      poseLandmarkerRef.current = null;
   147→    }
   148→    stopRecording();
   149→    stopCamera();
   150→  };
   151→
   152→  const startCamera = async () => {
   153→    try {
   154→      const stream = await navigator.mediaDevices.getUserMedia({
   155→        video: { width: 640, height: 480, facingMode: 'user' },
   156→        audio: false, // We don't need audio for pose analysis
   157→      });
   158→
   159→      streamRef.current = stream;
   160→
   161→      if (videoRef.current) {
   162→        videoRef.current.srcObject = stream;
   163→        await videoRef.current.play();
   164→      }
   165→
   166→      return stream;
   167→    } catch (error) {
   168→      console.error('Camera error:', error);
   169→      setPoseError('Could not access camera. Demo mode enabled.');
   170→      useMockDataRef.current = true;
   171→      setUseMockData(true);
   172→      return null;
   173→    }
   174→  };
   175→
   176→  const stopCamera = () => {
   177→    if (streamRef.current) {
   178→      streamRef.current.getTracks().forEach((track) => track.stop());
   179→      streamRef.current = null;
   180→    }
   181→    if (videoRef.current?.srcObject) {
   182→      const stream = videoRef.current.srcObject as MediaStream;
   183→      stream.getTracks().forEach((track) => track.stop());
   184→      videoRef.current.srcObject = null;
   185→    }
   186→  };
   187→
   188→  // Start MediaRecorder to capture the practice session
   189→  const startRecording = useCallback((stream: MediaStream) => {
   190→    if (!recordingEnabled || !stream) return;
   191→
   192→    try {
   193→      // Clear previous chunks
   194→      recordedChunksRef.current = [];
   195→
   196→      // Determine supported MIME type
   197→      const mimeType = MediaRecorder.isTypeSupported('video/webm;codecs=vp9')
   198→        ? 'video/webm;codecs=vp9'
   199→        : MediaRecorder.isTypeSupported('video/webm')
   200→        ? 'video/webm'
   201→        : 'video/mp4';
   202→
   203→      const mediaRecorder = new MediaRecorder(stream, {
   204→        mimeType,
   205→        videoBitsPerSecond: 2500000, // 2.5 Mbps
   206→      });
   207→
   208→      mediaRecorder.ondataavailable = (event) => {
   209→        if (event.data && event.data.size > 0) {
   210→          recordedChunksRef.current.push(event.data);
   211→        }
   212→      };
   213→
   214→      mediaRecorder.onstop = async () => {
   215→        // Recording stopped, will upload in stopSession
   216→        console.log('Recording stopped, chunks:', recordedChunksRef.current.length);
   217→      };
   218→
   219→      mediaRecorder.onerror = (event) => {
   220→        console.error('MediaRecorder error:', event);
   221→        setIsRecording(false);
   222→      };
   223→
   224→      mediaRecorderRef.current = mediaRecorder;
   225→      mediaRecorder.start(1000); // Collect data every second
   226→      setIsRecording(true);
   227→      console.log('Recording started with MIME type:', mimeType);
   228→    } catch (error) {
   229→      console.error('Failed to start recording:', error);
   230→      setRecordingEnabled(false);
   231→    }
   232→  }, [recordingEnabled]);
   233→
   234→  // Stop MediaRecorder
   235→  const stopRecording = useCallback(() => {
   236→    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
   237→      mediaRecorderRef.current.stop();
   238→      mediaRecorderRef.current = null;
   239→      setIsRecording(false);
   240→    }
   241→  }, []);
   242→
   243→  // Upload the recorded video
   244→  const uploadRecording = useCallback(async (sessionId: string): Promise<boolean> => {
   245→    if (recordedChunksRef.current.length === 0) {
   246→      console.log('No recorded chunks to upload');
   247→      return false;
   248→    }
   249→
   250→    setIsUploading(true);
   251→    setUploadProgress(0);
   252→    setUploadError(null);
   253→
   254→    try {
   255→      const { data: { user } } = await supabase.auth.getUser();
   256→      if (!user) throw new Error('Not authenticated');
   257→
   258→      // Determine MIME type from first chunk
   259→      const mimeType = recordedChunksRef.current[0]?.type || 'video/webm';
   260→
   261→      // Assemble the blob from recorded chunks
   262→      const blob = new Blob(recordedChunksRef.current, { type: mimeType });
   263→      console.log(`Uploading recording: ${(blob.size / 1024 / 1024).toFixed(2)}MB`);
   264→
   265→      // Upload to Supabase storage
   266→      const result = await uploadPracticeRecording(
   267→        blob,
   268→        user.id,
   269→        sessionId,
   270→        mimeType,
   271→        (progress) => setUploadProgress(progress)
   272→      );
   273→
   274→      if (!result) {
   275→        throw new Error('Upload failed');
   276→      }
   277→
   278→      // Update session with video metadata
   279→      const durationSeconds = sessionStartTime
   280→        ? Math.floor((Date.now() - sessionStartTime.getTime()) / 1000)
   281→        : 0;
   282→
   283→      await supabase
   284→        .from('sessions')
   285→        .update({
   286→          practice_video_path: result.path,
   287→          practice_video_mime: mimeType,
   288→          practice_video_duration_sec: durationSeconds,
   289→          practice_video_size_bytes: result.size,
   290→        })
   291→        .eq('id', sessionId);
   292→
   293→      console.log('Recording uploaded successfully:', result.path);
   294→      recordedChunksRef.current = []; // Clear chunks after successful upload
   295→      return true;
   296→    } catch (error) {
   297→      console.error('Failed to upload recording:', error);
   298→      setUploadError(error instanceof Error ? error.message : 'Upload failed');
   299→      return false;
   300→    } finally {
   301→      setIsUploading(false);
   302→    }
   303→  }, [sessionStartTime]);
   304→
   305→  const processFrame = useCallback(() => {
   306→    // Use refs to avoid stale closure issues
   307→    if (!isActiveRef.current) return;
   308→
   309→    if (useMockDataRef.current) {
   310→      // Generate mock data for demo
   311→      const metrics = generateMockMetrics();
   312→      setCurrentMetrics(metrics);
   313→
   314→      const { isGreen: green } = evaluateMetrics(metrics);
   315→      setIsGreen(green);
   316→
   317→      if (green) {
   318→        setGreenCount((c) => c + 1);
   319→        setFailingRules([]);
   320→      } else {
   321→        setRedCount((c) => c + 1);
   322→        setFailingRules(['Demo: Adjust form']);
   323→      }
   324→
   325→      // Draw mock skeleton on canvas
   326→      if (canvasRef.current && showSkeletonRef.current) {
   327→        const ctx = canvasRef.current.getContext('2d');
   328→        if (ctx) {
   329→          ctx.fillStyle = '#1f2937';
   330→          ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);
   331→
   332→          // Draw simple mock skeleton
   333→          const color = green ? '#22c55e' : '#ef4444';
   334→          ctx.strokeStyle = color;
   335→          ctx.lineWidth = 3;
   336→          ctx.fillStyle = color;
   337→
   338→          // Simple stick figure
   339→          const cx = 320;
   340→          const cy = 200;
   341→
   342→          // Head
   343→          ctx.beginPath();
   344→          ctx.arc(cx, cy - 60, 20, 0, Math.PI * 2);
   345→          ctx.fill();
   346→
   347→          // Body
   348→          ctx.beginPath();
   349→          ctx.moveTo(cx, cy - 40);
   350→          ctx.lineTo(cx, cy + 40);
   351→          ctx.stroke();
   352→
   353→          // Arms
   354→          ctx.beginPath();
   355→          ctx.moveTo(cx - 50, cy - 10);
   356→          ctx.lineTo(cx, cy - 20);
   357→          ctx.lineTo(cx + 60, cy - 40);
   358→          ctx.stroke();
   359→
   360→          // Legs
   361→          ctx.beginPath();
   362→          ctx.moveTo(cx - 30, cy + 100);
   363→          ctx.lineTo(cx, cy + 40);
   364→          ctx.lineTo(cx + 30, cy + 100);
   365→          ctx.stroke();
   366→        }
   367→      }
   368→    } else if (poseLandmarkerRef.current && videoRef.current && canvasRef.current) {
   369→      const video = videoRef.current;
   370→      const canvas = canvasRef.current;
   371→      const ctx = canvas.getContext('2d');
   372→
   373→      // Only process if we have a new frame
   374→      if (video.currentTime !== lastVideoTimeRef.current && ctx) {
   375→        lastVideoTimeRef.current = video.currentTime;
   376→
   377→        try {
   378→          // PHASE 1 FIX: Ensure timestamps are strictly monotonically increasing
   379→          // to prevent MediaPipe "Packet timestamp mismatch" errors
   380→          const rawTimestampMs = Math.floor(performance.now());
   381→          const timestampMs = Math.max(lastTimestampMsRef.current + 1, rawTimestampMs);
   382→          lastTimestampMsRef.current = timestampMs;
   383→
   384→          // Detect pose
   385→          const results = poseLandmarkerRef.current.detectForVideo(video, timestampMs);
   386→
   387→          // Clear canvas (transparent background - video shows through)
   388→          ctx.clearRect(0, 0, canvas.width, canvas.height);
   389→
   390→          if (results.landmarks && results.landmarks.length > 0) {
   391→            const poseLandmarks = results.landmarks[0];
   392→
   393→            // Convert to our format
   394→            const landmarks: PoseLandmark[] = poseLandmarks.map((lm: any) => ({
   395→              x: lm.x,
   396→              y: lm.y,
   397→              z: lm.z,
   398→              visibility: lm.visibility ?? 1.0,
   399→            }));
   400→
   401→            // Store current landmarks
   402→            currentLandmarksRef.current = landmarks;
   403→
   404→            const metrics = extractMetrics(landmarks);
   405→            setCurrentMetrics(metrics);
   406→
   407→            // Use rules engine for evaluation
   408→            const drillType = currentDrill?.keyframeType || 'general';
   409→            const evaluation = evaluateForm(landmarks, drillType, canvas.width);
   410→            setCurrentEvaluation(evaluation);
   411→
   412→            // Update green/red state
   413→            const green = evaluation.passed;
   414→            setIsGreen(green);
   415→
   416→            // Track failing rules
   417→            if (evaluation.failedRules.length > 0) {
   418→              setFailingRules(evaluation.failedRules.map(f => f.feedback));
   419→              setFocusMetric(evaluation.failedRules[0].rule.name);
   420→            } else {
   421→              setFailingRules([]);
   422→              setFocusMetric('');
   423→            }
   424→
   425→            // Update counts
   426→            if (green) {
   427→              setGreenCount((c) => c + 1);
   428→            } else {
   429→              setRedCount((c) => c + 1);
   430→            }
   431→
   432→            // Draw skeleton if enabled
   433→            if (showSkeletonRef.current) {
   434→              drawSkeleton(ctx, landmarks, green, canvas.width, canvas.height);
   435→            }
   436→          }
   437→        } catch (error) {
   438→          console.error('Error processing frame:', error);
   439→        }
   440→      }
   441→    }
   442→
   443→    // Continue animation loop
   444→    if (isActiveRef.current) {
   445→      animationRef.current = requestAnimationFrame(processFrame);
   446→    }
   447→  }, [currentDrill]);
   448→
   449→  const sendTick = async () => {
   450→    if (!currentMetrics) return;
   451→
   452→    try {
   453→      const response = await fetch('/api/practice/tick', {
   454→        method: 'POST',
   455→        headers: { 'Content-Type': 'application/json' },
   456→        body: JSON.stringify({
   457→          metrics_snapshot: currentMetrics,
   458→          drill_id: drillId || undefined,
   459→          session_id: sessionIdRef.current,
   460→        }),
   461→      });
   462→
   463→      if (response.ok) {
   464→        const data: TickResponse = await response.json();
   465→        setCoachingCue(data.cue);
   466→        if (data.focus_metric) {
   467→          setFocusMetric(data.focus_metric);
   468→        }
   469→        setIsGreen(data.is_green);
   470→        if (data.green_thresholds) {
   471→          setThresholds(data.green_thresholds as typeof DEFAULT_THRESHOLDS);
   472→        }
   473→      }
   474→    } catch (error) {
   475→      // Use local evaluation if API fails
   476→      if (currentEvaluation) {
   477→        if (currentEvaluation.failedRules.length > 0) {
   478→          setCoachingCue(currentEvaluation.failedRules[0].feedback);
   479→        } else {
   480→          setCoachingCue('Great form! Keep it up!');
   481→        }
   482→      }
   483→    }
   484→  };
   485→
   486→  const startSession = async () => {
   487→    // Create session in database
   488→    const { data: { user } } = await supabase.auth.getUser();
   489→
   490→    if (user) {
   491→      const { data, error } = await supabase
   492→        .from('sessions')
   493→        .insert({
   494→          user_id: user.id,
   495→          type: 'practice',
   496→          // created_at is auto-generated by database
   497→        })
   498→        .select()
   499→        .single();
   500→
   501→      if (!error && data) {
   502→        sessionIdRef.current = data.id;
   503→      }
   504→    }
   505→
   506→    // Set ref BEFORE state to ensure processFrame has correct value immediately
   507→    isActiveRef.current = true;
   508→    setIsActive(true);
   509→    setGreenCount(0);
   510→    setRedCount(0);
   511→    setSessionStartTime(new Date());
   512→    setCoachingCue('Let\'s go! Start your movement.');
   513→    setFailingRules([]);
   514→    setUploadError(null);
   515→
   516→    if (!useMockDataRef.current) {
   517→      const stream = await startCamera();
   518→      // Start recording if enabled and stream is available
   519→      if (stream && recordingEnabled) {
   520→        startRecording(stream);
   521→      }
   522→    }
   523→
   524→    // Start frame processing after camera is ready
   525→    setTimeout(() => {
   526→      if (isActiveRef.current) {
   527→        animationRef.current = requestAnimationFrame(processFrame);
   528→      }
   529→    }, 100);
   530→
   531→    // Start tick interval (every 1 second)
   532→    tickIntervalRef.current = setInterval(sendTick, 1000);
   533→  };
   534→
   535→  const stopSession = async () => {
   536→    // Set ref BEFORE state to stop animation loop immediately
   537→    isActiveRef.current = false;
   538→    setIsActive(false);
   539→
   540→    if (animationRef.current) {
   541→      cancelAnimationFrame(animationRef.current);
   542→      animationRef.current = null;
   543→    }
   544→    if (tickIntervalRef.current) {
   545→      clearInterval(tickIntervalRef.current);
   546→      tickIntervalRef.current = null;
   547→    }
   548→
   549→    // Stop recording first (before stopping camera)
   550→    stopRecording();
   551→
   552→    // Save session summary
   553→    const { data: { user } } = await supabase.auth.getUser();
   554→    if (user && sessionIdRef.current) {
   555→      const totalFrames = greenCount + redCount;
   556→      const avgScore = totalFrames > 0 ? (greenCount / totalFrames) * 100 : 0;
   557→      const durationSeconds = sessionStartTime ? (Date.now() - sessionStartTime.getTime()) / 1000 : 0;
   558→
   559→      await supabase
   560→        .from('sessions')
   561→        .update({
   562→          overall_score: Math.round(avgScore),
   563→          duration_seconds: durationSeconds,
   564→          summary: {
   565→            green_count: greenCount,
   566→            green_frames: greenCount,
   567→            red_count: redCount,
   568→            red_frames: redCount,
   569→            total_frames: totalFrames,
   570→            average_score: avgScore,
   571→            green_ratio: totalFrames > 0 ? greenCount / totalFrames : 0,
   572→            drill_id: drillId,
   573→            drill_type: drillId || 'general',
   574→            duration_seconds: durationSeconds,
   575→          },
   576→        })
   577→        .eq('id', sessionIdRef.current);
   578→
   579→      // Upload recording if we have one
   580→      if (recordedChunksRef.current.length > 0) {
   581→        await uploadRecording(sessionIdRef.current);
   582→      }
   583→    }
   584→
   585→    stopCamera();
   586→
   587→    setCoachingCue('Great session! Check your stats below.');
   588→    sessionIdRef.current = null;
   589→  };
   590→
   591→  const getElapsedTime = () => {
   592→    if (!sessionStartTime) return '0:00';
   593→    const elapsed = Math.floor((Date.now() - sessionStartTime.getTime()) / 1000);
   594→    const minutes = Math.floor(elapsed / 60);
   595→    const seconds = elapsed % 60;
   596→    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
   597→  };
   598→
   599→  const totalFrames = greenCount + redCount;
   600→  const greenRatio = totalFrames > 0 ? ((greenCount / totalFrames) * 100).toFixed(1) : '0';
   601→
   602→  return (
   603→    <div className="min-h-screen bg-gray-50">
   604→      <DashboardNav />
   605→
   606→      <main className="ml-64 p-8">
   607→        <div className="max-w-7xl mx-auto">
   608→          {/* Header */}
   609→          <div className="mb-8">
   610→            <h1 className="text-3xl font-bold text-gray-900">Practice Mode</h1>
   611→            <p className="text-gray-600 mt-1">
