     1‚Üí'use client';
     2‚Üí
     3‚Üíimport { useState, useEffect, useCallback } from 'react';
     4‚Üíimport Image from 'next/image';
     5‚Üíimport DashboardNav from '@/components/DashboardNav';
     6‚Üíimport { supabase } from '@/lib/supabase';
     7‚Üíimport type { RacketRecommendation } from '@/lib/types';
     8‚Üí
     9‚Üí// Track which images have failed to avoid repeated errors
    10‚Üíconst failedImages = new Set<string>();
    11‚Üí
    12‚Üíconst SKILL_LEVELS = [
    13‚Üí  { value: 'beginner', label: 'Beginner', description: 'New to badminton' },
    14‚Üí  { value: 'intermediate', label: 'Intermediate', description: '1-3 years experience' },
    15‚Üí  { value: 'advanced', label: 'Advanced', description: '3+ years, competitive play' },
    16‚Üí];
    17‚Üí
    18‚Üíconst PLAY_STYLES = [
    19‚Üí  { value: 'power', label: 'Power', description: 'Strong smashes and clears' },
    20‚Üí  { value: 'control', label: 'Control', description: 'Precise placement and drops' },
    21‚Üí  { value: 'all-round', label: 'All-Round', description: 'Balanced gameplay' },
    22‚Üí];
    23‚Üí
    24‚Üíconst WEAKNESSES = [
    25‚Üí  { value: 'smash_power', label: 'Smash Power' },
    26‚Üí  { value: 'defense', label: 'Defense' },
    27‚Üí  { value: 'footwork', label: 'Footwork' },
    28‚Üí  { value: 'net_play', label: 'Net Play' },
    29‚Üí  { value: 'endurance', label: 'Endurance' },
    30‚Üí  { value: 'consistency', label: 'Consistency' },
    31‚Üí];
    32‚Üí
    33‚Üíexport default function RacketPage() {
    34‚Üí  const [level, setLevel] = useState<string>('intermediate');
    35‚Üí  const [style, setStyle] = useState<string>('all-round');
    36‚Üí  const [weaknesses, setWeaknesses] = useState<string[]>([]);
    37‚Üí  const [loading, setLoading] = useState(false);
    38‚Üí  const [recommendations, setRecommendations] = useState<RacketRecommendation[]>([]);
    39‚Üí  const [favorites, setFavorites] = useState<Set<string>>(new Set());
    40‚Üí  const [error, setError] = useState<string | null>(null);
    41‚Üí
    42‚Üí  useEffect(() => {
    43‚Üí    loadFavorites();
    44‚Üí  }, []);
    45‚Üí
    46‚Üí  const loadFavorites = async () => {
    47‚Üí    const { data: { user } } = await supabase.auth.getUser();
    48‚Üí    if (!user) return;
    49‚Üí
    50‚Üí    try {
    51‚Üí      const { data, error } = await supabase
    52‚Üí        .from('racket_favorites')
    53‚Üí        .select('racket_id')
    54‚Üí        .eq('user_id', user.id);
    55‚Üí
    56‚Üí      // Gracefully handle if table doesn't exist
    57‚Üí      if (error) {
    58‚Üí        console.log('Favorites not available:', error.message);
    59‚Üí        return;
    60‚Üí      }
    61‚Üí
    62‚Üí      if (data) {
    63‚Üí        setFavorites(new Set(data.map((f) => f.racket_id)));
    64‚Üí      }
    65‚Üí    } catch (err) {
    66‚Üí      // Table may not exist - just use local state
    67‚Üí      console.log('Using local favorites only');
    68‚Üí    }
    69‚Üí  };
    70‚Üí
    71‚Üí  const toggleWeakness = (weakness: string) => {
    72‚Üí    setWeaknesses((prev) =>
    73‚Üí      prev.includes(weakness)
    74‚Üí        ? prev.filter((w) => w !== weakness)
    75‚Üí        : [...prev, weakness]
    76‚Üí    );
    77‚Üí  };
    78‚Üí
    79‚Üí  const getRecommendations = async () => {
    80‚Üí    if (weaknesses.length === 0) {
    81‚Üí      setError('Please select at least one weakness to improve');
    82‚Üí      return;
    83‚Üí    }
    84‚Üí
    85‚Üí    setError(null);
    86‚Üí    setLoading(true);
    87‚Üí
    88‚Üí    try {
    89‚Üí      const response = await fetch('/api/racket/recommend', {
    90‚Üí        method: 'POST',
    91‚Üí        headers: { 'Content-Type': 'application/json' },
    92‚Üí        body: JSON.stringify({
    93‚Üí          level,
    94‚Üí          weaknesses,
    95‚Üí          style_pref: style,
    96‚Üí        }),
    97‚Üí      });
    98‚Üí
    99‚Üí      if (!response.ok) {
   100‚Üí        throw new Error('Failed to get recommendations');
   101‚Üí      }
   102‚Üí
   103‚Üí      const data = await response.json();
   104‚Üí      setRecommendations(data.recommendations);
   105‚Üí    } catch (err) {
   106‚Üí      console.error('Error:', err);
   107‚Üí      setError('Failed to get recommendations. Please try again.');
   108‚Üí    } finally {
   109‚Üí      setLoading(false);
   110‚Üí    }
   111‚Üí  };
   112‚Üí
   113‚Üí  const toggleFavorite = async (racketId: string) => {
   114‚Üí    // Always toggle locally first for immediate UI feedback
   115‚Üí    setFavorites((prev) => {
   116‚Üí      const next = new Set(prev);
   117‚Üí      if (next.has(racketId)) {
   118‚Üí        next.delete(racketId);
   119‚Üí      } else {
   120‚Üí        next.add(racketId);
   121‚Üí      }
   122‚Üí      return next;
   123‚Üí    });
   124‚Üí
   125‚Üí    // Try to persist to database if user is logged in
   126‚Üí    const { data: { user } } = await supabase.auth.getUser();
   127‚Üí    if (!user) return;
   128‚Üí
   129‚Üí    try {
   130‚Üí      if (favorites.has(racketId)) {
   131‚Üí        // Remove from favorites
   132‚Üí        await supabase
   133‚Üí          .from('racket_favorites')
   134‚Üí          .delete()
   135‚Üí          .eq('user_id', user.id)
   136‚Üí          .eq('racket_id', racketId);
   137‚Üí      } else {
   138‚Üí        // Add to favorites
   139‚Üí        await supabase.from('racket_favorites').insert({
   140‚Üí          user_id: user.id,
   141‚Üí          racket_id: racketId,
   142‚Üí        });
   143‚Üí      }
   144‚Üí    } catch (err) {
   145‚Üí      // Table may not exist - favorites are still stored locally
   146‚Üí      console.log('Favorites stored locally only');
   147‚Üí    }
   148‚Üí  };
   149‚Üí
   150‚Üí  const openEbaySearch = (query: string) => {
   151‚Üí    const url = `https://www.ebay.com/sch/i.html?_nkw=${encodeURIComponent(query)}`;
   152‚Üí    window.open(url, '_blank');
   153‚Üí  };
   154‚Üí
   155‚Üí  return (
   156‚Üí    <div className="min-h-screen bg-gray-50">
   157‚Üí      <DashboardNav />
   158‚Üí
   159‚Üí      <main className="ml-64 p-4 lg:p-8 pt-16 lg:pt-8">
   160‚Üí        <div className="max-w-7xl mx-auto">
   161‚Üí          {/* Coach Header Card */}
   162‚Üí          <div className="coach-card rounded-2xl p-6 mb-8 relative overflow-hidden">
   163‚Üí            <div className="absolute top-0 right-0 w-48 h-48 opacity-5">
   164‚Üí              <div className="w-full h-full" style={{
   165‚Üí                backgroundImage: 'linear-gradient(to right, #166534 1px, transparent 1px), linear-gradient(to bottom, #166534 1px, transparent 1px)',
   166‚Üí                backgroundSize: '20px 20px'
   167‚Üí              }} />
   168‚Üí            </div>
   169‚Üí            <div className="relative z-10">
   170‚Üí              <div className="flex items-center gap-3 mb-2">
   171‚Üí                <span className="text-3xl">&#127992;</span>
   172‚Üí                <h1 className="text-2xl lg:text-3xl font-bold text-gray-900">Racket Finder</h1>
   173‚Üí              </div>
   174‚Üí              <p className="text-gray-600">Get personalized racket recommendations based on your playing profile</p>
   175‚Üí            </div>
   176‚Üí          </div>
   177‚Üí
   178‚Üí          {/* Selection Form */}
   179‚Üí          <div className="coach-card rounded-xl p-6 mb-8">
   180‚Üí            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
   181‚Üí              {/* Skill Level */}
   182‚Üí              <div>
   183‚Üí                <h3 className="font-semibold text-gray-900 mb-3">Skill Level</h3>
   184‚Üí                <div className="space-y-2">
   185‚Üí                  {SKILL_LEVELS.map((lvl) => (
   186‚Üí                    <button
   187‚Üí                      key={lvl.value}
   188‚Üí                      onClick={() => setLevel(lvl.value)}
   189‚Üí                      className={`w-full text-left p-3 rounded-lg border transition ${
   190‚Üí                        level === lvl.value
   191‚Üí                          ? 'border-primary-500 bg-primary-50'
   192‚Üí                          : 'border-gray-200 hover:border-gray-300'
   193‚Üí                      }`}
   194‚Üí                    >
   195‚Üí                      <p className="font-medium text-gray-900">{lvl.label}</p>
   196‚Üí                      <p className="text-sm text-gray-500">{lvl.description}</p>
   197‚Üí                    </button>
   198‚Üí                  ))}
   199‚Üí                </div>
   200‚Üí              </div>
   201‚Üí
   202‚Üí              {/* Play Style */}
   203‚Üí              <div>
   204‚Üí                <h3 className="font-semibold text-gray-900 mb-3">Play Style</h3>
   205‚Üí                <div className="space-y-2">
   206‚Üí                  {PLAY_STYLES.map((s) => (
   207‚Üí                    <button
   208‚Üí                      key={s.value}
   209‚Üí                      onClick={() => setStyle(s.value)}
   210‚Üí                      className={`w-full text-left p-3 rounded-lg border transition ${
   211‚Üí                        style === s.value
   212‚Üí                          ? 'border-primary-500 bg-primary-50'
   213‚Üí                          : 'border-gray-200 hover:border-gray-300'
   214‚Üí                      }`}
   215‚Üí                    >
   216‚Üí                      <p className="font-medium text-gray-900">{s.label}</p>
   217‚Üí                      <p className="text-sm text-gray-500">{s.description}</p>
   218‚Üí                    </button>
   219‚Üí                  ))}
   220‚Üí                </div>
   221‚Üí              </div>
   222‚Üí
   223‚Üí              {/* Weaknesses */}
   224‚Üí              <div>
   225‚Üí                <h3 className="font-semibold text-gray-900 mb-3">
   226‚Üí                  Areas to Improve
   227‚Üí                </h3>
   228‚Üí                <div className="flex flex-wrap gap-2">
   229‚Üí                  {WEAKNESSES.map((w) => (
   230‚Üí                    <button
   231‚Üí                      key={w.value}
   232‚Üí                      onClick={() => toggleWeakness(w.value)}
   233‚Üí                      className={`px-3 py-2 rounded-lg border text-sm transition ${
   234‚Üí                        weaknesses.includes(w.value)
   235‚Üí                          ? 'border-primary-500 bg-primary-50 text-primary-700'
   236‚Üí                          : 'border-gray-200 text-gray-600 hover:border-gray-300'
   237‚Üí                      }`}
   238‚Üí                    >
   239‚Üí                      {w.label}
   240‚Üí                    </button>
   241‚Üí                  ))}
   242‚Üí                </div>
   243‚Üí                <p className="text-xs text-gray-500 mt-2">
   244‚Üí                  Select at least one area
   245‚Üí                </p>
   246‚Üí              </div>
   247‚Üí            </div>
   248‚Üí
   249‚Üí            {error && (
   250‚Üí              <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded-lg text-red-700 text-sm">
   251‚Üí                {error}
   252‚Üí              </div>
   253‚Üí            )}
   254‚Üí
   255‚Üí            <div className="mt-6 flex justify-center">
   256‚Üí              <button
   257‚Üí                onClick={getRecommendations}
   258‚Üí                disabled={loading}
   259‚Üí                className="px-8 py-3 bg-gradient-to-r from-primary-500 to-primary-600 text-white rounded-xl font-medium hover:from-primary-600 hover:to-primary-700 transition-all shadow-md disabled:opacity-50 quick-action-btn"
   260‚Üí              >
   261‚Üí                {loading ? (
   262‚Üí                  <span className="flex items-center space-x-2">
   263‚Üí                    <svg
   264‚Üí                      className="animate-spin h-5 w-5"
   265‚Üí                      fill="none"
   266‚Üí                      viewBox="0 0 24 24"
   267‚Üí                    >
   268‚Üí                      <circle
   269‚Üí                        className="opacity-25"
   270‚Üí                        cx="12"
   271‚Üí                        cy="12"
   272‚Üí                        r="10"
   273‚Üí                        stroke="currentColor"
   274‚Üí                        strokeWidth="4"
   275‚Üí                      />
   276‚Üí                      <path
   277‚Üí                        className="opacity-75"
   278‚Üí                        fill="currentColor"
   279‚Üí                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
   280‚Üí                      />
   281‚Üí                    </svg>
   282‚Üí                    <span>Finding Rackets...</span>
   283‚Üí                  </span>
   284‚Üí                ) : (
   285‚Üí                  'Get Recommendations'
   286‚Üí                )}
   287‚Üí              </button>
   288‚Üí            </div>
   289‚Üí          </div>
   290‚Üí
   291‚Üí          {/* Recommendations Grid */}
   292‚Üí          {recommendations.length > 0 && (
   293‚Üí            <div>
   294‚Üí              <div className="flex items-center gap-2 mb-4">
   295‚Üí                <span className="text-xl">&#127942;</span>
   296‚Üí                <h2 className="text-xl font-bold text-gray-900">Coach Recommendations</h2>
   297‚Üí              </div>
   298‚Üí              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
   299‚Üí                {recommendations.map((racket, index) => (
   300‚Üí                  <RacketCard
   301‚Üí                    key={racket.id}
   302‚Üí                    racket={racket}
   303‚Üí                    index={index}
   304‚Üí                    isFavorite={favorites.has(racket.id)}
   305‚Üí                    onToggleFavorite={() => toggleFavorite(racket.id)}
   306‚Üí                    onOpenEbay={() => openEbaySearch(racket.ebay_search_query)}
   307‚Üí                  />
   308‚Üí                ))}
   309‚Üí              </div>
   310‚Üí            </div>
   311‚Üí          )}
   312‚Üí
   313‚Üí          {/* Empty State */}
   314‚Üí          {recommendations.length === 0 && !loading && (
   315‚Üí            <div className="text-center py-12">
   316‚Üí              <div className="w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
   317‚Üí                <span className="text-4xl">üè∏</span>
   318‚Üí              </div>
   319‚Üí              <h3 className="text-lg font-medium text-gray-900 mb-2">
   320‚Üí                Find Your Perfect Racket
   321‚Üí              </h3>
   322‚Üí              <p className="text-gray-500 max-w-md mx-auto">
   323‚Üí                Select your skill level, playing style, and areas you want to
   324‚Üí                improve, then click &quot;Get Recommendations&quot; to see personalized
   325‚Üí                racket suggestions.
   326‚Üí              </p>
   327‚Üí            </div>
   328‚Üí          )}
   329‚Üí        </div>
   330‚Üí      </main>
   331‚Üí    </div>
   332‚Üí  );
   333‚Üí}
