   335‚Üí// Racket Card Component with image handling
   336‚Üífunction RacketCard({
   337‚Üí  racket,
   338‚Üí  index,
   339‚Üí  isFavorite,
   340‚Üí  onToggleFavorite,
   341‚Üí  onOpenEbay,
   342‚Üí}: {
   343‚Üí  racket: RacketRecommendation;
   344‚Üí  index: number;
   345‚Üí  isFavorite: boolean;
   346‚Üí  onToggleFavorite: () => void;
   347‚Üí  onOpenEbay: () => void;
   348‚Üí}) {
   349‚Üí  const [imageError, setImageError] = useState(false);
   350‚Üí  const [imageLoading, setImageLoading] = useState(true);
   351‚Üí
   352‚Üí  // Check if this image has already failed
   353‚Üí  const hasFailedBefore = racket.image_url && failedImages.has(racket.image_url);
   354‚Üí  const showImage = racket.image_url && !imageError && !hasFailedBefore;
   355‚Üí
   356‚Üí  const handleImageError = useCallback(() => {
   357‚Üí    if (racket.image_url) {
   358‚Üí      // Log the failing URL once
   359‚Üí      if (!failedImages.has(racket.image_url)) {
   360‚Üí        console.warn('Racket image failed to load:', racket.image_url);
   361‚Üí        failedImages.add(racket.image_url);
   362‚Üí      }
   363‚Üí    }
   364‚Üí    setImageError(true);
   365‚Üí    setImageLoading(false);
   366‚Üí  }, [racket.image_url]);
   367‚Üí
   368‚Üí  const handleImageLoad = useCallback(() => {
   369‚Üí    setImageLoading(false);
   370‚Üí  }, []);
   371‚Üí
   372‚Üí  return (
   373‚Üí    <div className="bg-white rounded-xl shadow-sm overflow-hidden hover:shadow-md transition">
   374‚Üí      {/* Rank Badge */}
   375‚Üí      <div className="relative">
   376‚Üí        <div className="bg-gray-100 h-48 flex items-center justify-center overflow-hidden">
   377‚Üí          {showImage ? (
   378‚Üí            <>
   379‚Üí              {imageLoading && (
   380‚Üí                <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
   381‚Üí                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500" />
   382‚Üí                </div>
   383‚Üí              )}
   384‚Üí              <Image
   385‚Üí                src={racket.image_url!}
   386‚Üí                alt={`${racket.brand} ${racket.name}`}
   387‚Üí                width={192}
   388‚Üí                height={192}
   389‚Üí                className={`object-contain max-h-44 transition-opacity ${imageLoading ? 'opacity-0' : 'opacity-100'}`}
   390‚Üí                style={{ width: 'auto', height: 'auto' }}
   391‚Üí                onError={handleImageError}
   392‚Üí                onLoad={handleImageLoad}
   393‚Üí                unoptimized // Use unoptimized for external eBay images
   394‚Üí              />
   395‚Üí            </>
   396‚Üí          ) : (
   397‚Üí            <span className="text-6xl">üè∏</span>
   398‚Üí          )}
   399‚Üí        </div>
   400‚Üí        <div className="absolute top-3 left-3 w-8 h-8 bg-primary-500 rounded-full flex items-center justify-center">
   401‚Üí          <span className="text-white font-bold text-sm">
   402‚Üí            #{index + 1}
   403‚Üí          </span>
   404‚Üí        </div>
   405‚Üí        <button
   406‚Üí          onClick={onToggleFavorite}
   407‚Üí          className="absolute top-3 right-3 w-8 h-8 bg-white rounded-full flex items-center justify-center shadow-sm hover:shadow-md transition"
   408‚Üí        >
   409‚Üí          <svg
   410‚Üí            className={`w-5 h-5 ${
   411‚Üí              isFavorite
   412‚Üí                ? 'text-red-500 fill-current'
   413‚Üí                : 'text-gray-400'
   414‚Üí            }`}
   415‚Üí            fill={isFavorite ? 'currentColor' : 'none'}
   416‚Üí            stroke="currentColor"
   417‚Üí            viewBox="0 0 24 24"
   418‚Üí          >
   419‚Üí            <path
   420‚Üí              strokeLinecap="round"
   421‚Üí              strokeLinejoin="round"
   422‚Üí              strokeWidth={2}
   423‚Üí              d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
   424‚Üí            />
   425‚Üí          </svg>
   426‚Üí        </button>
   427‚Üí      </div>
   428‚Üí
   429‚Üí      <div className="p-4">
   430‚Üí        {/* Header */}
   431‚Üí        <div className="flex items-start justify-between mb-2">
   432‚Üí          <div>
   433‚Üí            <h3 className="font-semibold text-gray-900">
   434‚Üí              {racket.name}
   435‚Üí            </h3>
   436‚Üí            <p className="text-sm text-gray-500">{racket.brand}</p>
   437‚Üí          </div>
   438‚Üí          <div className="text-right">
   439‚Üí            <p className="font-bold text-primary-600">
   440‚Üí              {racket.match_score}%
   441‚Üí            </p>
   442‚Üí            <p className="text-xs text-gray-500">match</p>
   443‚Üí          </div>
   444‚Üí        </div>
   445‚Üí
