     1→'use client';
     2→
     3→import { useState, useEffect, useRef, Suspense, lazy, useCallback, useMemo } from 'react';
     4→import { useSearchParams } from 'next/navigation';
     5→import Link from 'next/link';
     6→import DashboardNav from '@/components/DashboardNav';
     7→import { supabase, uploadVideo, getVideoUrl } from '@/lib/supabase';
     8→import type { Session, TrajectoryPoint, StrategyResult } from '@/lib/types';
     9→import {
    10→  createPlaybackEngine,
    11→  PlaybackEngine,
    12→  updatePlaybackTime,
    13→  type TimeScale,
    14→  type ActiveView,
    15→  type PlaybackEngineRefs,
    16→} from '@/lib/playback-engine';
    17→import {
    18→  COURT_SPEC,
    19→  calculateCourtScale,
    20→  metersToCanvas,
    21→  normalizedToCanvas,
    22→  zoneToPosition,
    23→  getZoneName,
    24→} from '@/lib/courtSpec';
    25→import {
    26→  analyzeRally,
    27→  type RallyAnalysisResult,
    28→  type PerShotAnalysis,
    29→  type ShotRecommendation,
    30→  type RallyState,
    31→  RECOMMENDATION_LINE_STYLES,
    32→  PHASE_COLORS,
    33→  PHASE_NEXT_ACTION,
    34→} from '@/lib/strategy_engine';
    35→
    36→// PHASE 4: Lazy load 3D component to avoid SSR issues
    37→const CourtTrajectory3D = lazy(() => import('@/components/CourtTrajectory3D'));
    38→
    39→// ============================================
    40→// Strategy Mode Page
    41→// ============================================
    42→
    43→type StrategyStep = 'select' | 'confirm' | 'processing' | 'results';
    44→
    45→interface HistorySession extends Session {
    46→  thumbnail_url?: string;
    47→}
    48→
    49→// Strategy Recommendation types (legacy format - still used for display)
    50→interface StrategyRecommendation {
    51→  id: string;
    52→  name: string;
    53→  reason: string;
    54→  confidence: number;
    55→  jumpToVideoTimeSec?: number;
    56→  trajectory2D: TrajectoryPoint[];
    57→  trajectory3D?: TrajectoryPoint[];
    58→  // New fields for enhanced recommendations
    59→  shotType?: string;
    60→  targetZone?: number;
    61→  score?: number;
    62→  rationale?: Array<{ type: string; description: string; impact: number }>;
    63→}
    64→
    65→// Rally State Chip Component
    66→function RallyStateChip({
    67→  state,
    68→  onStateClick,
    69→}: {
    70→  state: RallyState | null;
    71→  onStateClick?: () => void;
    72→}) {
    73→  if (!state) return null;
    74→
    75→  const phaseStyle = PHASE_COLORS[state.phase] || PHASE_COLORS.neutral;
    76→  const nextAction = PHASE_NEXT_ACTION[state.phase] || 'Build';
    77→  const pressurePercent = Math.round(state.pressure * 100);
    78→
    79→  return (
    80→    <button
    81→      onClick={onStateClick}
    82→      className={`px-4 py-2 rounded-full ${phaseStyle.bg} ${phaseStyle.text} font-medium text-sm flex items-center gap-2 transition hover:opacity-80`}
    83→    >
    84→      <span className="capitalize">{state.phase}</span>
    85→      <span className="text-xs opacity-75">(pressure {pressurePercent}%)</span>
    86→      <span className="mx-1">&rarr;</span>
    87→      <span>{nextAction}</span>
    88→    </button>
    89→  );
    90→}
    91→
    92→// Shot Timeline Component
    93→function ShotTimeline({
    94→  shots,
    95→  currentShotIndex,
    96→  onShotSelect,
    97→}: {
    98→  shots: PerShotAnalysis[];
    99→  currentShotIndex: number;
   100→  onShotSelect: (index: number) => void;
   101→}) {
   102→  if (shots.length === 0) return null;
   103→
   104→  return (
   105→    <div className="flex items-center gap-1 overflow-x-auto py-2 px-1">
   106→      {shots.map((shot, index) => {
   107→        const isActive = index === currentShotIndex;
   108→        const phaseColor =
   109→          shot.features.rallyState.phase === 'attack'
   110→            ? 'bg-green-500'
   111→            : shot.features.rallyState.phase === 'defense'
   112→            ? 'bg-red-500'
   113→            : 'bg-gray-400';
   114→
   115→        return (
   116→          <button
   117→            key={index}
   118→            onClick={() => onShotSelect(index)}
   119→            className={`flex flex-col items-center p-1 rounded transition ${
   120→              isActive ? 'bg-primary-100 ring-2 ring-primary-500' : 'hover:bg-gray-100'
   121→            }`}
   122→            title={`Shot ${index + 1}: ${shot.shot.type}`}
   123→          >
   124→            <div
   125→              className={`w-3 h-3 rounded-full ${phaseColor} ${
   126→                isActive ? 'ring-2 ring-primary-500 ring-offset-1' : ''
   127→              }`}
   128→            />
   129→            <span className="text-xs text-gray-500 mt-0.5">{index + 1}</span>
   130→          </button>
   131→        );
   132→      })}
   133→    </div>
   134→  );
   135→}
   136→
   137→// Rationale Item Component
   138→function RationaleItem({
   139→  rationale,
   140→}: {
   141→  rationale: { type: string; description: string; impact: number };
   142→}) {
   143→  const isPositive = rationale.impact > 0;
   144→  const impactPercent = Math.abs(Math.round(rationale.impact * 100));
   145→
   146→  return (
   147→    <div className="flex items-start gap-2 text-sm">
   148→      <span
   149→        className={`w-5 h-5 flex items-center justify-center rounded-full text-xs font-bold ${
   150→          isPositive ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
   151→        }`}
   152→      >
   153→        {isPositive ? '+' : '-'}
   154→      </span>
   155→      <div className="flex-1">
   156→        <p className="text-gray-700">{rationale.description}</p>
   157→        <p className="text-xs text-gray-400">
   158→          {rationale.type.replace(/_/g, ' ')} ({impactPercent}% impact)
   159→        </p>
   160→      </div>
   161→    </div>
   162→  );
   163→}
   164→
   165→function StrategyContent() {
   166→  const searchParams = useSearchParams();
   167→  const sessionIdParam = searchParams.get('session');
   168→
   169→  // Step state
   170→  const [step, setStep] = useState<StrategyStep>('select');
   171→  const [selectedSource, setSelectedSource] = useState<'upload' | 'history' | null>(null);
   172→
   173→  // Video/session state
   174→  const [videoFile, setVideoFile] = useState<File | null>(null);
   175→  const [videoUrl, setVideoUrl] = useState<string | null>(null);
   176→  const [sessionId, setSessionId] = useState<string | null>(sessionIdParam);
   177→  const [selectedHistorySession, setSelectedHistorySession] = useState<HistorySession | null>(null);
   178→
   179→  // History drawer
   180→  const [showHistoryDrawer, setShowHistoryDrawer] = useState(false);
   181→  const [historySessions, setHistorySessions] = useState<HistorySession[]>([]);
   182→  const [loadingHistory, setLoadingHistory] = useState(false);
   183→
   184→  // Processing state
   185→  const [processing, setProcessing] = useState(false);
   186→  const [processProgress, setProcessProgress] = useState(0);
   187→  const [error, setError] = useState<string | null>(null);
   188→
   189→  // Results state
   190→  const [strategyResult, setStrategyResult] = useState<StrategyResult | null>(null);
   191→  const [recommendations, setRecommendations] = useState<StrategyRecommendation[]>([]);
   192→  const [selectedRecommendation, setSelectedRecommendation] = useState<StrategyRecommendation | null>(null);
   193→  const [coachingPoints, setCoachingPoints] = useState<string[]>([]);
   194→
   195→  // Enhanced strategy state
   196→  const [rallyAnalysis, setRallyAnalysis] = useState<RallyAnalysisResult | null>(null);
   197→  const [currentShotIndex, setCurrentShotIndex] = useState(0);
   198→  const [currentRallyState, setCurrentRallyState] = useState<RallyState | null>(null);
   199→  const [showAllPaths, setShowAllPaths] = useState(true);
   200→
   201→  // Refs
   202→  const fileInputRef = useRef<HTMLInputElement>(null);
   203→  const videoRef = useRef<HTMLVideoElement>(null);
   204→
   205→  // Load from session param
   206→  useEffect(() => {
   207→    if (sessionIdParam) {
   208→      loadSessionFromHistory(sessionIdParam);
   209→    }
   210→  }, [sessionIdParam]);
   211→
   212→  // Load history sessions
   213→  const loadHistorySessions = async () => {
   214→    setLoadingHistory(true);
   215→    try {
   216→      const { data: { user } } = await supabase.auth.getUser();
   217→      if (!user) return;
   218→
   219→      const { data, error } = await supabase
   220→        .from('sessions')
   221→        .select('*')
   222→        .eq('user_id', user.id)
   223→        .in('type', ['analytics', 'practice'])
   224→        .not('video_url', 'is', null)
   225→        .order('created_at', { ascending: false })
   226→        .limit(50);
   227→
   228→      if (error) throw error;
   229→      setHistorySessions(data || []);
   230→    } catch (err) {
   231→      console.error('Error loading history:', err);
   232→    } finally {
   233→      setLoadingHistory(false);
   234→    }
   235→  };
   236→
   237→  // Load session from history
   238→  const loadSessionFromHistory = async (id: string) => {
   239→    try {
   240→      const { data, error } = await supabase
   241→        .from('sessions')
   242→        .select('*')
   243→        .eq('id', id)
   244→        .single();
   245→
   246→      if (error) throw error;
   247→
   248→      setSelectedHistorySession(data);
   249→      setSessionId(data.id);
   250→      setVideoUrl(data.video_url);
   251→      setSelectedSource('history');
   252→      setStep('confirm');
   253→    } catch (err) {
   254→      console.error('Error loading session:', err);
   255→      setError('Failed to load session');
   256→    }
   257→  };
   258→
   259→  // Handle file upload
   260→  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
   261→    const file = e.target.files?.[0];
   262→    if (!file) return;
   263→
   264→    if (!file.type.startsWith('video/')) {
   265→      setError('Please select a video file');
   266→      return;
   267→    }
   268→
   269→    // Max file size 100MB (TUS resumable upload handles large files)
   270→    if (file.size > 100 * 1024 * 1024) {
   271→      setError('File size must be less than 100MB. Please compress your video before uploading.');
   272→      return;
   273→    }
   274→
   275→    setError(null);
   276→    setVideoFile(file);
   277→    setVideoUrl(URL.createObjectURL(file));
   278→    setSelectedSource('upload');
   279→    setStep('confirm');
   280→  };
   281→
   282→  // Handle history selection
   283→  const handleHistorySelect = (session: HistorySession) => {
   284→    setSelectedHistorySession(session);
   285→    setSessionId(session.id);
   286→    setVideoUrl(session.video_url || null);
   287→    setSelectedSource('history');
   288→    setShowHistoryDrawer(false);
   289→    setStep('confirm');
   290→  };
   291→
   292→  // Handle confirmation: No
   293→  const handleConfirmNo = () => {
   294→    setVideoFile(null);
   295→    setVideoUrl(null);
   296→    setSessionId(null);
   297→    setSelectedHistorySession(null);
   298→    setSelectedSource(null);
   299→    setStep('select');
   300→    setError(null);
   301→    setRecommendations([]);
   302→    setSelectedRecommendation(null);
   303→  };
   304→
   305→  // Handle confirmation: Yes
   306→  const handleConfirmYes = async () => {
   307→    setStep('processing');
   308→    setProcessing(true);
   309→    setProcessProgress(0);
   310→    setError(null);
   311→
   312→    try {
   313→      const { data: { user } } = await supabase.auth.getUser();
   314→      if (!user) throw new Error('Not authenticated. Please log in again.');
   315→
   316→      // If new upload, create session and upload video
   317→      let currentSessionId = sessionId;
   318→      let uploadedVideoUrl = videoUrl;
   319→
   320→      if (selectedSource === 'upload' && videoFile) {
   321→        // Upload video with progress tracking
   322→        // This is critical - do NOT create session if upload fails
   323→        setProcessProgress(5);
   324→
   325→        let videoPath: string | null = null;
   326→        try {
   327→          videoPath = await uploadVideo(videoFile, user.id, (progress) => {
   328→            // Upload progress: 5-25%
   329→            setProcessProgress(5 + Math.round(progress * 0.2));
   330→          });
   331→
   332→          if (!videoPath) {
   333→            throw new Error('Upload returned empty path');
   334→          }
   335→
   336→          // Get public URL
   337→          uploadedVideoUrl = getVideoUrl(videoPath);
   338→          console.log('Strategy video uploaded:', videoPath);
   339→
   340→        } catch (uploadError) {
   341→          console.error('Strategy video upload failed:', uploadError);
   342→          const uploadErrorMsg = uploadError instanceof Error
   343→            ? uploadError.message
   344→            : 'Failed to upload video. Please try again.';
   345→          throw new Error(uploadErrorMsg);
   346→        }
   347→
   348→        // Create session only AFTER successful upload
   349→        setProcessProgress(25);
   350→        const { data: session, error: sessionError } = await supabase
   351→          .from('sessions')
   352→          .insert({
   353→            user_id: user.id,
   354→            type: 'strategy',
   355→            video_path: videoPath,
   356→            video_url: uploadedVideoUrl,
   357→            filename: videoFile.name,
   358→            status: 'processing',
   359→          })
   360→          .select()
   361→          .single();
   362→
   363→        if (sessionError) {
   364→          console.error('Session creation error:', sessionError);
   365→          throw new Error('Failed to create analysis session');
   366→        }
   367→
   368→        currentSessionId = session.id;
   369→        setSessionId(session.id);
   370→      } else if (sessionId) {
   371→        // Update existing session type to strategy
   372→        await supabase
   373→          .from('sessions')
   374→          .update({ status: 'processing' })
   375→          .eq('id', sessionId);
   376→      }
   377→
   378→      if (!currentSessionId) throw new Error('No session ID');
   379→
   380→      // Generate strategy analysis
   381→      setProcessProgress(30);
   382→      const result = await generateStrategyAnalysis(currentSessionId);
   383→      setProcessProgress(100);
   384→
   385→      setStrategyResult(result);
   386→      setCoachingPoints(result.coaching_points || []);
   387→
   388→      // Generate recommendations - prefer new engine if available
   389→      let generatedRecs: StrategyRecommendation[];
   390→      if (rallyAnalysis && rallyAnalysis.shots.length > 0) {
   391→        generatedRecs = generateRecommendationsFromAnalysis(rallyAnalysis, 0);
   392→      } else {
   393→        generatedRecs = generateRecommendations(result);
   394→      }
   395→
   396→      setRecommendations(generatedRecs);
   397→      if (generatedRecs.length > 0) {
   398→        setSelectedRecommendation(generatedRecs[0]);
   399→      }
   400→
   401→      // Update session status
   402→      await supabase
   403→        .from('sessions')
   404→        .update({ status: 'ready' })
   405→        .eq('id', currentSessionId);
   406→
   407→      setStep('results');
   408→    } catch (err) {
   409→      console.error('Strategy processing error:', err);
   410→      const errorMessage = err instanceof Error ? err.message : 'Failed to analyze video. Please try again.';
   411→      setError(errorMessage);
   412→      setStep('confirm');
   413→    } finally {
   414→      setProcessing(false);
   415→    }
   416→  };
   417→
   418→  // Convert ShotRecommendation from engine to display format
   419→  const convertToDisplayRecommendation = (
   420→    rec: ShotRecommendation,
   421→    index: number
   422→  ): StrategyRecommendation => {
   423→    // Convert path polyline to TrajectoryPoint format
   424→    const trajectory: TrajectoryPoint[] = rec.pathPolyline.map((p, i) => ({
   425→      x: p.x,
   426→      y: p.y,
   427→      timestamp: i * 500, // Simulate timestamps
   428→      court_x: p.x,
   429→      court_y: p.y,
   430→    }));
   431→
   432→    // Generate human-readable name based on shot type and zone
   433→    const shotTypeNames: Record<string, string> = {
   434→      clear: 'Deep Clear',
   435→      drop: 'Drop Shot',
   436→      smash: 'Smash Attack',
   437→      drive: 'Flat Drive',
   438→      net: 'Net Play',
   439→      lift: 'Defensive Lift',
   440→      push: 'Push Shot',
   441→      serve: 'Service',
   442→      unknown: 'Shot',
   443→    };
   444→
   445→    const zoneName = getZoneName(rec.targetZone);
   446→    const name = `${shotTypeNames[rec.shotType] || 'Shot'} to ${zoneName}`;
   447→
   448→    // Generate reason from rationales
   449→    const positiveRationales = rec.rationale.filter(r => r.impact > 0);
   450→    const reason = positiveRationales.length > 0
   451→      ? positiveRationales.map(r => r.description).join('. ')
   452→      : `Target ${zoneName} with ${rec.shotType} for tactical advantage.`;
   453→
   454→    return {
   455→      id: rec.id,
   456→      name,
   457→      reason,
   458→      confidence: Math.round(rec.score),
   459→      jumpToVideoTimeSec: index * 2,
   460→      trajectory2D: trajectory,
   461→      trajectory3D: trajectory,
   462→      shotType: rec.shotType,
   463→      targetZone: rec.targetZone,
   464→      score: rec.score,
   465→      rationale: rec.rationale,
   466→    };
   467→  };
   468→
   469→  // Generate strategy recommendations from result (enhanced with strategy engine)
   470→  const generateRecommendationsFromAnalysis = (
   471→    analysis: RallyAnalysisResult,
   472→    shotIndex: number
   473→  ): StrategyRecommendation[] => {
   474→    if (!analysis.shots || analysis.shots.length === 0) {
   475→      return [];
   476→    }
   477→
   478→    const shotAnalysis = analysis.shots[Math.min(shotIndex, analysis.shots.length - 1)];
   479→    if (!shotAnalysis || !shotAnalysis.recommendations) {
   480→      return [];
   481→    }
   482→
   483→    return shotAnalysis.recommendations.map((rec, idx) =>
   484→      convertToDisplayRecommendation(rec, idx)
   485→    );
   486→  };
   487→
   488→  // Legacy recommendation generator (fallback)
   489→  const generateRecommendations = (result: StrategyResult): StrategyRecommendation[] => {
   490→    // Generate multiple recommendation variants
   491→    const recommendations: StrategyRecommendation[] = [];
   492→
   493→    // Recommendation 1: Optimal path (existing refined trajectory)
   494→    recommendations.push({
   495→      id: 'optimal-deep',
   496→      name: 'Deep Clear Strategy',
   497→      reason: 'Push opponent to the back court with deep clears to create attacking opportunities.',
   498→      confidence: 92,
   499→      jumpToVideoTimeSec: 2,
   500→      trajectory2D: result.refined_trajectory,
   501→      trajectory3D: result.refined_trajectory,
   502→    });
   503→
   504→    // Recommendation 2: Cross-court variant
   505→    const crossCourtTrajectory = result.refined_trajectory.map((p, i) => ({
   506→      ...p,
   507→      x: i % 2 === 0 ? 0.2 : 0.8, // Alternate sides
   508→      court_x: i % 2 === 0 ? 0.2 : 0.8,
   509→    }));
   510→    recommendations.push({
   511→      id: 'cross-court',
   512→      name: 'Cross-Court Attack',
   513→      reason: 'Use sharp cross-court angles to move opponent laterally and open up straight line kills.',
   514→      confidence: 87,
   515→      jumpToVideoTimeSec: 4,
   516→      trajectory2D: crossCourtTrajectory,
   517→      trajectory3D: crossCourtTrajectory,
   518→    });
   519→
   520→    // Recommendation 3: Net rush
   521→    const netRushTrajectory = result.refined_trajectory.map((p, i) => ({
   522→      ...p,
   523→      y: i % 2 === 0 ? 0.3 : 0.55, // Closer to net
   524→      court_y: i % 2 === 0 ? 0.3 : 0.55,
   525→    }));
   526→    recommendations.push({
   527→      id: 'net-rush',
   528→      name: 'Net Domination',
   529→      reason: 'Control the front court with tight net shots to force weak lifts and create smash opportunities.',
   530→      confidence: 78,
   531→      jumpToVideoTimeSec: 6,
   532→      trajectory2D: netRushTrajectory,
   533→      trajectory3D: netRushTrajectory,
   534→    });
   535→
   536→    return recommendations;
   537→  };
   538→
   539→  // Generate strategy analysis using the new strategy engine
   540→  const generateStrategyAnalysis = async (sessionId: string): Promise<StrategyResult> => {
   541→    // Simulate initial processing
   542→    for (let i = 30; i <= 50; i += 5) {
   543→      await new Promise((resolve) => setTimeout(resolve, 300));
   544→      setProcessProgress(i);
   545→    }
   546→
   547→    // Generate mock trajectory data (in production, this would come from video analysis)
   548→    const originalTrajectory: TrajectoryPoint[] = [];
   549→    const refinedTrajectory: TrajectoryPoint[] = [];
   550→
   551→    // Generate a rally pattern with more realistic data
   552→    const rallyPoints = 8;
   553→    for (let i = 0; i < rallyPoints; i++) {
   554→      const isEven = i % 2 === 0;
   555→
   556→      // Original trajectory (player's actual shots)
   557→      originalTrajectory.push({
   558→        x: 0.3 + Math.random() * 0.4,
   559→        y: isEven ? 0.15 + Math.random() * 0.2 : 0.65 + Math.random() * 0.2,
   560→        timestamp: i * 1000, // Use milliseconds
   561→        court_x: 0.3 + Math.random() * 0.4,
   562→        court_y: isEven ? 0.2 : 0.8,
   563→      });
   564→
   565→      // Refined trajectory (optimal shots)
   566→      refinedTrajectory.push({
   567→        x: isEven ? 0.7 : 0.3,
   568→        y: isEven ? 0.1 : 0.9,
   569→        timestamp: i * 1000,
   570→        court_x: isEven ? 0.7 : 0.3,
   571→        court_y: isEven ? 0.1 : 0.9,
   572→      });
   573→    }
   574→
   575→    setProcessProgress(60);
   576→
   577→    // Use the strategy engine to analyze the trajectory
   578→    let analysis: RallyAnalysisResult | null = null;
   579→    try {
   580→      analysis = analyzeRally(sessionId, originalTrajectory);
   581→      setRallyAnalysis(analysis);
   582→
   583→      // Set initial rally state
   584→      if (analysis.shots.length > 0) {
   585→        setCurrentShotIndex(0);
   586→        setCurrentRallyState(analysis.shots[0].features.rallyState);
   587→      }
   588→    } catch (err) {
   589→      console.error('Strategy engine analysis failed, using fallback:', err);
   590→    }
   591→
   592→    setProcessProgress(80);
   593→
   594→    // Generate coaching points based on analysis
   595→    const coachingPointsOptions = [
   596→      'Your shots tend to land mid-court. Push for deeper clears to the back tramlines.',
   597→      'Good variation in cross-court angles. Consider adding more straight drops to surprise opponents.',
   598→      'Rally patterns show predictable sequences. Mix up the pace with sudden smashes.',
   599→      'Effective use of the net area. Maintain the pressure with quicker net exchanges.',
   600→      'Recovery positioning is slightly late. Focus on split-step timing after each shot.',
   601→      'Strong defensive clears under pressure. Work on transitioning to attack faster.',
   602→      'Shot selection in the forecourt is excellent. Add deceptive flicks to your arsenal.',
   603→      'Backhand shots are going wide. Adjust your grip and follow-through angle.',
   604→    ];
   605→
   606→    // Add analysis-specific insights if available
   607→    if (analysis && analysis.summary) {
