  1053→// ============================================
  1054→// Court Trajectory Animation Component
  1055→// Uses PlaybackEngine for proper lifecycle management
  1056→// ============================================
  1057→
  1058→interface CourtTrajectoryAnimationProps {
  1059→  originalTrajectory: TrajectoryPoint[];
  1060→  refinedTrajectory: TrajectoryPoint[];
  1061→  allRecommendations?: StrategyRecommendation[];
  1062→  selectedRecommendationIndex?: number;
  1063→}
  1064→
  1065→// Speed options
  1066→const SPEED_OPTIONS: { value: TimeScale; label: string }[] = [
  1067→  { value: 0.25, label: '0.25x' },
  1068→  { value: 0.5, label: '0.5x' },
  1069→  { value: 1, label: '1x' },
  1070→  { value: 2, label: '2x' },
  1071→];
  1072→
  1073→function CourtTrajectoryAnimation({
  1074→  originalTrajectory,
  1075→  refinedTrajectory,
  1076→  allRecommendations,
  1077→  selectedRecommendationIndex = -1,
  1078→}: CourtTrajectoryAnimationProps) {
  1079→  const canvasRef = useRef<HTMLCanvasElement>(null);
  1080→
  1081→  // UI state (for display only)
  1082→  const [isPlaying, setIsPlaying] = useState(true);
  1083→  const [showOriginal, setShowOriginal] = useState(true);
  1084→  const [showRefined, setShowRefined] = useState(true);
  1085→  const [speed, setSpeed] = useState<TimeScale>(1);
  1086→  const [activeView, setActiveView] = useState<ActiveView>('2d');
  1087→  const [progress, setProgress] = useState(0);
  1088→
  1089→  // Playback engine refs - these are the source of truth for animation state
  1090→  const engineRef = useRef<PlaybackEngineRefs>(createPlaybackEngine());
  1091→  const animationRef = useRef<number | null>(null);
  1092→  const mountedRef = useRef(true);
  1093→
  1094→  // Keep refs for callbacks to avoid stale closures
  1095→  const showOriginalRef = useRef(showOriginal);
  1096→  const showRefinedRef = useRef(showRefined);
  1097→  const originalTrajectoryRef = useRef(originalTrajectory);
  1098→  const refinedTrajectoryRef = useRef(refinedTrajectory);
  1099→  const allRecommendationsRef = useRef(allRecommendations);
  1100→  const selectedRecommendationIndexRef = useRef(selectedRecommendationIndex);
  1101→
  1102→  // Sync refs with state
  1103→  useEffect(() => {
  1104→    showOriginalRef.current = showOriginal;
  1105→  }, [showOriginal]);
  1106→
  1107→  useEffect(() => {
  1108→    showRefinedRef.current = showRefined;
  1109→  }, [showRefined]);
  1110→
  1111→  useEffect(() => {
  1112→    originalTrajectoryRef.current = originalTrajectory;
  1113→  }, [originalTrajectory]);
  1114→
  1115→  useEffect(() => {
  1116→    refinedTrajectoryRef.current = refinedTrajectory;
  1117→  }, [refinedTrajectory]);
  1118→
