  // Processing state
  const [processing, setProcessing] = useState(false);
  const [processProgress, setProcessProgress] = useState(0);
  const [error, setError] = useState<string | null>(null);

  // Results state
  const [strategyResult, setStrategyResult] = useState<StrategyResult | null>(null);
  const [recommendations, setRecommendations] = useState<StrategyRecommendation[]>([]);
  const [selectedRecommendation, setSelectedRecommendation] = useState<StrategyRecommendation | null>(null);
  const [coachingPoints, setCoachingPoints] = useState<string[]>([]);

  // Enhanced strategy state
  const [rallyAnalysis, setRallyAnalysis] = useState<RallyAnalysisResult | null>(null);
  const [currentShotIndex, setCurrentShotIndex] = useState(0);
  const [currentRallyState, setCurrentRallyState] = useState<RallyState | null>(null);
  const [showAllPaths, setShowAllPaths] = useState(true);

  // Refs
  const fileInputRef = useRef<HTMLInputElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);

