      let generatedRecs: StrategyRecommendation[];
      if (rallyAnalysis && rallyAnalysis.shots.length > 0) {
        generatedRecs = generateRecommendationsFromAnalysis(rallyAnalysis, 0);
      } else {
        generatedRecs = generateRecommendations(result);
      }

      setRecommendations(generatedRecs);
      if (generatedRecs.length > 0) {
        setSelectedRecommendation(generatedRecs[0]);
      }

      // Update session status
      await supabase
        .from('sessions')
        .update({ status: 'ready' })
        .eq('id', currentSessionId);

      setStep('results');
    } catch (err) {
      console.error('Strategy processing error:', err);
      const errorMessage = err instanceof Error ? err.message : 'Failed to analyze video. Please try again.';
