      timestamp: i * 500, // Simulate timestamps
      court_x: p.x,
      court_y: p.y,
    }));

    // Generate human-readable name based on shot type and zone
    const shotTypeNames: Record<string, string> = {
      clear: 'Deep Clear',
      drop: 'Drop Shot',
      smash: 'Smash Attack',
      drive: 'Flat Drive',
      net: 'Net Play',
      lift: 'Defensive Lift',
      push: 'Push Shot',
      serve: 'Service',
      unknown: 'Shot',
    };

    const zoneName = getZoneName(rec.targetZone);
    const name = `${shotTypeNames[rec.shotType] || 'Shot'} to ${zoneName}`;

    // Generate reason from rationales
    const positiveRationales = rec.rationale.filter(r => r.impact > 0);
    const reason = positiveRationales.length > 0
      ? positiveRationales.map(r => r.description).join('. ')
      : `Target ${zoneName} with ${rec.shotType} for tactical advantage.`;

    return {
      id: rec.id,
      name,
      reason,
      confidence: Math.round(rec.score),
      jumpToVideoTimeSec: index * 2,
      trajectory2D: trajectory,
      trajectory3D: trajectory,
      shotType: rec.shotType,
      targetZone: rec.targetZone,
      score: rec.score,
      rationale: rec.rationale,
    };
  };

  // Generate strategy recommendations from result (enhanced with strategy engine)
  const generateRecommendationsFromAnalysis = (
    analysis: RallyAnalysisResult,
    shotIndex: number
  ): StrategyRecommendation[] => {
    if (!analysis.shots || analysis.shots.length === 0) {
      return [];
    }

    const shotAnalysis = analysis.shots[Math.min(shotIndex, analysis.shots.length - 1)];
    if (!shotAnalysis || !shotAnalysis.recommendations) {
      return [];
    }

    return shotAnalysis.recommendations.map((rec, idx) =>
      convertToDisplayRecommendation(rec, idx)
    );
  };

  // Legacy recommendation generator (fallback)
  const generateRecommendations = (result: StrategyResult): StrategyRecommendation[] => {
    // Generate multiple recommendation variants
