'use client';

import { useState, useEffect, useRef } from 'react';
import Link from 'next/link';
import { supabase } from '@/lib/supabase';
import type { User } from '@supabase/supabase-js';

/*
 * IMAGE ASSETS
 * ============
 * Place your own images in /public/images/ and update these paths.
 * See /public/images/README.md for specs.
 *
 * hero-team-bg.png  â†’ Team lineup (Layer 1 background)
 * hero-jumper.png   â†’ Single player transparent PNG (Layer 3 foreground)
 * arena-gear.png    â†’ Rackets/equipment closeup
 * arena-match.png   â†’ Tournament/match action shot
 * arena-drills.png  â†’ Training drills photo
 * court-tactics.png â†’ Court overhead / tactical view
 */
const IMAGES = {
  // Layer 1: Team background (replace with your badminton team photo)
  heroBg: "/images/hero-team-bg.png",
  // Layer 3: Jump smash player (MUST be transparent PNG)
  heroJumper: "/images/hero-jumper.png",
