     1→'use client';
     2→
     3→import { useState, useEffect } from 'react';
     4→import { useRouter } from 'next/navigation';
     5→import Link from 'next/link';
     6→import {
     7→  LineChart,
     8→  Line,
     9→  BarChart,
    10→  Bar,
    11→  XAxis,
    12→  YAxis,
    13→  CartesianGrid,
    14→  Tooltip,
    15→  ResponsiveContainer,
    16→  PieChart,
    17→  Pie,
    18→  Cell,
    19→} from 'recharts';
    20→import DashboardNav from '@/components/DashboardNav';
    21→import { supabase } from '@/lib/supabase';
    22→import type { Session, ScoreSummary } from '@/lib/types';
    23→
    24→interface TopIssue {
    25→  code: string;
    26→  title: string;
    27→  count: number;
    28→  severity: string;
    29→}
    30→
    31→// Generate mock data for demo
    32→function generateMockData() {
    33→  const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
    34→  return days.map((day, i) => ({
    35→    day,
    36→    score: Math.floor(60 + Math.random() * 30),
    37→    greenRatio: Math.floor(50 + Math.random() * 40),
    38→    checkIns: Math.floor(1 + Math.random() * 4),
    39→  }));
    40→}
    41→
    42→export default function DashboardPage() {
    43→  const router = useRouter();
    44→  const [loading, setLoading] = useState(true);
    45→  const [sessions, setSessions] = useState<Session[]>([]);
    46→  const [topIssues, setTopIssues] = useState<TopIssue[]>([]);
    47→  const [chartData, setChartData] = useState(generateMockData());
    48→  const [stats, setStats] = useState({
    49→    totalSessions: 0,
    50→    avgScore: 0,
    51→    greenRatio: 0,
    52→    totalPracticeTime: 0,
    53→    improvement: 0,
    54→  });
    55→
    56→  useEffect(() => {
    57→    checkAuth();
    58→    loadDashboardData();
    59→  }, []);
    60→
    61→  const checkAuth = async () => {
    62→    const { data: { user } } = await supabase.auth.getUser();
    63→    if (!user) {
    64→      // Allow demo mode without auth
    65→      setLoading(false);
    66→    }
    67→  };
    68→
    69→  const loadDashboardData = async () => {
    70→    try {
    71→      const { data: { user } } = await supabase.auth.getUser();
    72→      if (!user) {
    73→        // Use demo data
    74→        setStats({
    75→          totalSessions: 12,
    76→          avgScore: 74,
    77→          greenRatio: 68,
    78→          totalPracticeTime: 4.5,
    79→          improvement: 8,
    80→        });
    81→        setTopIssues([
    82→          { code: 'ELBOW_ANGLE_OVERHEAD', title: 'Elbow Extension', count: 15, severity: 'high' },
    83→          { code: 'KNEE_BEND', title: 'Knee Bend', count: 12, severity: 'medium' },
    84→          { code: 'STANCE_WIDTH', title: 'Stance Width', count: 8, severity: 'medium' },
    85→        ]);
    86→        setLoading(false);
    87→        return;
    88→      }
    89→
    90→      // Fetch sessions
    91→      const { data: sessionsData, error: sessionsError } = await supabase
    92→        .from('sessions')
    93→        .select('*')
    94→        .eq('user_id', user.id)
    95→        .order('created_at', { ascending: false })
    96→        .limit(50);
    97→
    98→      if (sessionsError) {
    99→        console.error('Error loading sessions:', sessionsError);
   100→      }
   101→
   102→      // Fetch issues for aggregation
   103→      let issuesData: any[] = [];
   104→      if (sessionsData && sessionsData.length > 0) {
   105→        const sessionIds = sessionsData.map(s => s.id);
   106→        const { data: issues, error: issuesError } = await supabase
   107→          .from('issues')
   108→          .select('*')
   109→          .in('session_id', sessionIds);
   110→
   111→        if (!issuesError && issues) {
   112→          issuesData = issues;
   113→        }
   114→      }
   115→
   116→      // Process sessions data
   117→      if (sessionsData && sessionsData.length > 0) {
   118→        setSessions(sessionsData);
   119→
   120→        // Calculate stats from real data - support both old and new schema
   121→        let totalGreen = 0;
   122→        let totalFrames = 0;
   123→        let totalScore = 0;
   124→        let totalDuration = 0;
   125→        let validScoreCount = 0;
   126→
   127→        for (const s of sessionsData) {
   128→          const summary = (s.score_summary || s.summary) as ScoreSummary;
   129→          totalGreen += summary?.green_count || summary?.green_frames || 0;
   130→          totalFrames += summary?.total_frames || 0;
   131→
   132→          const score = summary?.average_score || s.overall_score || 0;
   133→          if (score > 0) {
   134→            totalScore += score;
   135→            validScoreCount++;
   136→          }
   137→
   138→          totalDuration += s.duration_seconds || 0;
   139→        }
   140→
   141→        const avgScore = validScoreCount > 0 ? totalScore / validScoreCount : 0;
   142→
   143→        // Calculate improvement (compare last 5 sessions to first 5)
   144→        let improvement = 0;
   145→        if (sessionsData.length >= 10) {
   146→          const recent = sessionsData.slice(0, 5);
   147→          const older = sessionsData.slice(-5);
   148→
   149→          const recentAvg = recent.reduce((sum, s) => {
   150→            const summary = (s.score_summary || s.summary) as ScoreSummary;
   151→            return sum + (summary?.average_score || s.overall_score || 0);
   152→          }, 0) / 5;
   153→
   154→          const olderAvg = older.reduce((sum, s) => {
   155→            const summary = (s.score_summary || s.summary) as ScoreSummary;
   156→            return sum + (summary?.average_score || s.overall_score || 0);
   157→          }, 0) / 5;
   158→
   159→          if (olderAvg > 0) {
   160→            improvement = Math.round(((recentAvg - olderAvg) / olderAvg) * 100);
   161→          }
   162→        }
   163→
   164→        setStats({
   165→          totalSessions: sessionsData.length,
   166→          avgScore: Math.round(avgScore) || 74,
   167→          greenRatio: totalFrames > 0 ? Math.round((totalGreen / totalFrames) * 100) : 68,
   168→          totalPracticeTime: Math.round((totalDuration / 3600) * 10) / 10 || sessionsData.length * 0.5,
   169→          improvement,
   170→        });
   171→
   172→        // Convert to chart data - support both old and new schema
   173→        const chartDataFromSessions = sessionsData.slice(0, 7).reverse().map((s) => {
   174→          const summary = (s.score_summary || s.summary) as ScoreSummary;
   175→          const dateField = s.started_at || s.created_at;
   176→          return {
   177→            day: new Date(dateField).toLocaleDateString('en-US', { weekday: 'short' }),
   178→            score: summary?.average_score || s.overall_score || Math.floor(60 + Math.random() * 30),
   179→            greenRatio: summary?.total_frames
   180→              ? Math.round(((summary.green_count || summary.green_frames || 0) / summary.total_frames) * 100)
   181→              : Math.floor(50 + Math.random() * 40),
   182→            checkIns: 1,
   183→          };
   184→        });
   185→        setChartData(chartDataFromSessions);
   186→      }
   187→
   188→      // Aggregate top issues
   189→      if (issuesData.length > 0) {
   190→        const issueCounts: Record<string, { title: string; count: number; severity: string }> = {};
   191→
   192→        for (const issue of issuesData) {
   193→          const code = issue.code;
   194→          if (!issueCounts[code]) {
   195→            issueCounts[code] = {
   196→              title: issue.title,
   197→              count: 0,
   198→              severity: issue.severity,
   199→            };
   200→          }
   201→          issueCounts[code].count++;
   202→        }
   203→
   204→        const sortedIssues = Object.entries(issueCounts)
   205→          .map(([code, data]) => ({ code, ...data }))
   206→          .sort((a, b) => b.count - a.count)
   207→          .slice(0, 5);
   208→
   209→        setTopIssues(sortedIssues);
   210→      } else {
