     7→/**
     8→ * Wrapper to handle AbortError from Supabase auth locks.
     9→ * When the auth lock's AbortController is aborted before acquisition,
    10→ * this retries the operation once after a short delay.
    11→ */
    12→async function withLockErrorHandling<T>(fn: () => Promise<T>): Promise<T> {
    13→  try {
    14→    return await fn();
    15→  } catch (error) {
    16→    if (error instanceof Error && error.name === 'AbortError') {
    17→      console.warn('Auth lock aborted, retrying...', error);
    18→      // Retry once after a short delay
    19→      await new Promise(resolve => setTimeout(resolve, 100));
    20→      return await fn();
    21→    }
    22→    throw error;
    23→  }
    24→}
