    26→// Constants for upload handling
    27→const RESUMABLE_THRESHOLD = 6 * 1024 * 1024; // 6MB - use TUS for files larger than this
    28→const MAX_FILE_SIZE = 50 * 1024 * 1024; // 50MB max file size (Supabase free tier limit)
    29→const STORAGE_BUCKET = 'videos';
    30→
    31→// Helper to format file size for user-friendly display
    32→function formatFileSize(bytes: number): string {
    33→  if (bytes < 1024) return bytes + ' B';
    34→  if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
    35→  return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
    36→}
    37→const PRACTICE_RECORDINGS_BUCKET = 'practice-recordings';
    38→
    39→// Client-side Supabase client (lazy initialization)
    40→let _supabase: SupabaseClient | null = null;
    41→
    42→export const supabase = (() => {
    43→  if (!_supabase && supabaseUrl && supabaseAnonKey) {
    44→    _supabase = createClient(supabaseUrl, supabaseAnonKey);
    45→  }
    46→  // Return a mock client if env vars not available (for SSR)
    47→  if (!_supabase) {
    48→    return createClient(
    49→      supabaseUrl || 'https://placeholder.supabase.co',
    50→      supabaseAnonKey || 'placeholder-key'
    51→    );
    52→  }
    53→  return _supabase;
    54→})();
    55→
    56→// Server-side Supabase client (for API routes)
    57→export function createServerClient() {
    58→  const url = process.env.NEXT_PUBLIC_SUPABASE_URL || supabaseUrl;
    59→  const key = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || supabaseAnonKey;
    60→
    61→  if (!url || !key) {
    62→    throw new Error('Supabase environment variables not configured');
    63→  }
    64→
    65→  return createClient(url, key, {
    66→    auth: {
    67→      autoRefreshToken: false,
    68→      persistSession: false,
    69→    },
    70→  });
    71→}
    72→
    73→// Helper to get user from request
    74→export async function getUserFromRequest(request: Request) {
    75→  const authHeader = request.headers.get('authorization');
    76→  if (!authHeader?.startsWith('Bearer ')) {
    77→    return null;
    78→  }
    79→
    80→  const token = authHeader.split(' ')[1];
    81→  const serverClient = createServerClient();
    82→
    83→  const { data: { user }, error } = await serverClient.auth.getUser(token);
    84→
    85→  if (error || !user) {
    86→    return null;
    87→  }
    88→
    89→  return user;
    90→}
    91→
    92→// Sanitize filename to remove non-ASCII characters for Supabase storage
    93→function sanitizeFileName(fileName: string): string {
    94→  // Get file extension
    95→  const lastDot = fileName.lastIndexOf('.');
    96→  const ext = lastDot > 0 ? fileName.slice(lastDot) : '';
    97→  const baseName = lastDot > 0 ? fileName.slice(0, lastDot) : fileName;
    98→
    99→  // Replace non-ASCII characters with empty string, keep alphanumeric, dash, underscore
   100→  const sanitized = baseName
   101→    .replace(/[^\x00-\x7F]/g, '') // Remove non-ASCII
   102→    .replace(/[^a-zA-Z0-9_-]/g, '_') // Replace special chars with underscore
   103→    .replace(/_+/g, '_') // Collapse multiple underscores
   104→    .replace(/^_|_$/g, '') // Trim underscores from start/end
   105→    || 'video'; // Default name if nothing remains
   106→
   107→  return sanitized + ext;
   108→}
