   120→// Storage helpers
   121→
   122→/**
   123→ * Upload video with automatic selection of standard or TUS resumable upload
   124→ * based on file size. Files > 6MB use TUS protocol for reliability.
   125→ *
   126→ * @param file - The video file to upload
   127→ * @param userId - The user ID for path organization
   128→ * @param onProgress - Optional progress callback (0-100)
   129→ * @returns UploadResult with path, bucket, and publicUrl
   130→ * @throws Error if upload fails
   131→ */
   132→export async function uploadVideo(
   133→  file: File,
   134→  userId: string,
   135→  onProgress?: UploadProgressCallback
   136→): Promise<string | null> {
   137→  // Validate file size
   138→  if (file.size > MAX_FILE_SIZE) {
   139→    const currentSize = formatFileSize(file.size);
   140→    const maxSize = formatFileSize(MAX_FILE_SIZE);
   141→    console.error(`Upload error: File size (${currentSize}) exceeds ${maxSize} limit`);
   142→    throw new Error(
   143→      `Video file is too large (${currentSize}). Maximum allowed size is ${maxSize}. ` +
   144→      `Please compress your video using a tool like HandBrake or an online video compressor, ` +
   145→      `or try recording at a lower resolution (720p recommended).`
   146→    );
   147→  }
   148→
   149→  // Validate file type
   150→  if (!file.type.startsWith('video/')) {
   151→    throw new Error('Invalid file type. Please upload a video file.');
   152→  }
   153→
   154→  const sanitizedName = sanitizeFileName(file.name);
   155→  const fileName = `${userId}/${Date.now()}-${sanitizedName}`;
   156→
   157→  // Use TUS resumable upload for large files
   158→  if (file.size > RESUMABLE_THRESHOLD) {
   159→    console.log(`Using TUS resumable upload for ${file.name} (${(file.size / 1024 / 1024).toFixed(2)}MB)`);
   160→    return await uploadVideoResumable(file, fileName, onProgress);
   161→  }
   162→
   163→  // Use standard upload for small files
   164→  console.log(`Using standard upload for ${file.name} (${(file.size / 1024 / 1024).toFixed(2)}MB)`);
   165→  return await uploadVideoStandard(file, fileName, onProgress);
   166→}
   167→
   168→/**
   169→ * Standard upload for files < 6MB
   170→ */
   171→async function uploadVideoStandard(
   172→  file: File,
   173→  fileName: string,
   174→  onProgress?: UploadProgressCallback
   175→): Promise<string | null> {
   176→  // Report initial progress
   177→  onProgress?.(10);
   178→
   179→  const { data, error } = await supabase.storage
   180→    .from(STORAGE_BUCKET)
   181→    .upload(fileName, file, {
   182→      cacheControl: '3600',
   183→      upsert: false
   184→    });
   185→
   186→  if (error) {
   187→    console.error('Upload error:', error);
   188→    if (error.message?.includes('exceeded the maximum allowed size')) {
   189→      throw new Error('File size exceeds storage limit. Please compress your video before uploading.');
   190→    }
   191→    if (error.message?.includes('Bucket not found')) {
   192→      throw new Error('Storage not configured. Please contact support.');
   193→    }
   194→    throw new Error(`Failed to upload video: ${error.message}`);
   195→  }
   196→
   197→  onProgress?.(100);
   198→  return data.path;
   199→}
