   201→/**
   202→ * TUS resumable upload for files >= 6MB
   203→ * Uses tus-js-client for reliable large file uploads with resume capability
   204→ */
   205→async function uploadVideoResumable(
   206→  file: File,
   207→  fileName: string,
   208→  onProgress?: UploadProgressCallback
   209→): Promise<string | null> {
   210→  return new Promise(async (resolve, reject) => {
   211→    try {
   212→      // Get the current session for auth token (with lock error handling)
   213→      const { data: { session } } = await withLockErrorHandling(() =>
   214→        supabase.auth.getSession()
   215→      );
   216→      if (!session?.access_token) {
   217→        throw new Error('Not authenticated. Please log in again.');
   218→      }
   219→
   220→      const upload = new tus.Upload(file, {
   221→        endpoint: `${supabaseUrl}/storage/v1/upload/resumable`,
   222→        retryDelays: [0, 1000, 3000, 5000], // Retry delays in ms
   223→        chunkSize: 6 * 1024 * 1024, // 6MB chunks
   224→        headers: {
   225→          authorization: `Bearer ${session.access_token}`,
   226→          'x-upsert': 'false', // Don't overwrite existing files
   227→        },
   228→        uploadDataDuringCreation: true,
   229→        removeFingerprintOnSuccess: true,
   230→        metadata: {
   231→          bucketName: STORAGE_BUCKET,
   232→          objectName: fileName,
   233→          contentType: file.type,
   234→          cacheControl: '3600',
   235→        },
   236→        onError: (error) => {
   237→          console.error('TUS upload error:', error);
   238→          // Parse error message for user-friendly display
   239→          let message = 'Failed to upload video.';
   240→          const errorStr = error.message || error.toString();
   241→
   242→          if (errorStr.includes('413') || errorStr.includes('exceeded') || errorStr.includes('Maximum size')) {
   243→            const maxSize = formatFileSize(MAX_FILE_SIZE);
   244→            message = `File size exceeds storage limit (${maxSize}). Please compress your video using HandBrake or an online compressor, or record at 720p resolution.`;
   245→          } else if (errorStr.includes('network') || errorStr.includes('fetch')) {
   246→            message = 'Network error. Please check your internet connection and try again.';
   247→          } else if (errorStr.includes('unauthorized') || errorStr.includes('401')) {
   248→            message = 'Session expired. Please log in again.';
   249→          } else if (errorStr.includes('403') || errorStr.includes('forbidden')) {
   250→            message = 'Access denied. Please check your permissions or log in again.';
   251→          }
   252→          reject(new Error(message));
   253→        },
   254→        onProgress: (bytesUploaded, bytesTotal) => {
   255→          const percentage = Math.round((bytesUploaded / bytesTotal) * 100);
   256→          onProgress?.(percentage);
   257→        },
   258→        onSuccess: () => {
   259→          console.log('TUS upload completed:', fileName);
   260→          onProgress?.(100);
   261→          resolve(fileName);
   262→        },
   263→      });
   264→
   265→      // Check for previous upload to resume
   266→      const previousUploads = await upload.findPreviousUploads();
   267→      if (previousUploads.length > 0) {
   268→        console.log('Resuming previous upload...');
   269→        upload.resumeFromPreviousUpload(previousUploads[0]);
   270→      }
   271→
   272→      // Start the upload
   273→      upload.start();
   274→    } catch (error) {
   275→      console.error('TUS setup error:', error);
   276→      reject(error);
   277→    }
   278→  });
   279→}
