   361→/**
   362→ * Upload a practice session recording to storage
   363→ * Uses the practice-recordings bucket with user-scoped paths
   364→ *
   365→ * @param blob - The video blob from MediaRecorder
   366→ * @param userId - The user ID for path organization
   367→ * @param sessionId - The practice session ID
   368→ * @param mimeType - The MIME type of the recording
   369→ * @param onProgress - Optional progress callback
   370→ * @returns Object with path and metadata, or null on failure
   371→ */
   372→export async function uploadPracticeRecording(
   373→  blob: Blob,
   374→  userId: string,
   375→  sessionId: string,
   376→  mimeType: string = 'video/webm',
   377→  onProgress?: UploadProgressCallback
   378→): Promise<{ path: string; size: number } | null> {
   379→  // Validate size
   380→  if (blob.size > MAX_FILE_SIZE) {
   381→    console.error('Practice recording too large:', blob.size);
   382→    throw new Error('Recording too large. Maximum size is 100MB.');
   383→  }
   384→
   385→  // Normalize MIME type - strip codec specification (e.g., "video/webm;codecs=vp9" -> "video/webm")
   386→  // Supabase storage doesn't accept MIME types with codec parameters
   387→  const baseMimeType = mimeType.split(';')[0].trim();
   388→
   389→  // Determine file extension from MIME type
   390→  const ext = baseMimeType.includes('mp4') ? 'mp4' : 'webm';
   391→  const fileName = `${userId}/${sessionId}.${ext}`;
   392→
   393→  try {
   394→    // Report initial progress
   395→    onProgress?.(5);
   396→
   397→    // Get current session for auth (with lock error handling)
   398→    const { data: { session } } = await withLockErrorHandling(() =>
   399→      supabase.auth.getSession()
   400→    );
   401→    if (!session?.access_token) {
   402→      throw new Error('Not authenticated');
   403→    }
   404→
   405→    // Use TUS for large files
   406→    if (blob.size > RESUMABLE_THRESHOLD) {
   407→      console.log(`Using TUS upload for practice recording (${(blob.size / 1024 / 1024).toFixed(2)}MB)`);
   408→      const path = await uploadPracticeRecordingResumable(blob, fileName, baseMimeType, session.access_token, onProgress);
   409→      return path ? { path, size: blob.size } : null;
   410→    }
   411→
   412→    // Standard upload for small files
   413→    onProgress?.(10);
   414→    const { data, error } = await supabase.storage
   415→      .from(PRACTICE_RECORDINGS_BUCKET)
   416→      .upload(fileName, blob, {
   417→        contentType: baseMimeType,
   418→        cacheControl: '3600',
   419→        upsert: true,
   420→      });
   421→
   422→    if (error) {
   423→      console.error('Practice recording upload error:', error);
   424→      if (error.message?.includes('Bucket not found')) {
   425→        throw new Error('Storage bucket not configured. Please run the Supabase migration.');
   426→      }
   427→      throw new Error(`Upload failed: ${error.message}`);
   428→    }
   429→
   430→    onProgress?.(100);
   431→    return { path: data.path, size: blob.size };
   432→  } catch (error) {
   433→    console.error('Practice recording upload failed:', error);
   434→    throw error;
   435→  }
   436→}
