export default function Progress({ 
  value = 0, 
  max = 100,
  label,
  showPercent = true,
  variant = 'violet',
  className = '',
}) {
  const percentage = Math.round((value / max) * 100)

  const colors = {
    violet: 'bg-accent-violet',
    pink: 'bg-accent-pink',
    blue: 'bg-accent-blue',
    green: 'bg-accent-green',
  }

  return (
    <div className={`flex flex-col gap-2 ${className}`}>
      {(label || showPercent) && (
        <div className="flex justify-between text-sm">
          {label && <span className="text-text-muted">{label}</span>}
          {showPercent && <span className="font-medium text-text-primary">{percentage}%</span>}
        </div>
      )}
      <div className="h-3 bg-white/40 rounded-full overflow-hidden shadow-clay-input">
        <div 
          className={`h-full rounded-full transition-all duration-slow ease-smooth ${colors[variant]}`}
          style={{ width: `${percentage}%` }}
        />
      </div>
    </div>
  )
}
