@import "tailwindcss";

/* ========================================
   CLAYMORPHISM DESIGN SYSTEM TOKENS
   High-Fidelity Clay Aesthetic
   ======================================== */

@theme {
  /* ---- Canvas & Background ---- */
  --color-canvas: #F4F1FA;
  --color-canvas-dark: #E8E4F0;

  /* ---- Text Colors ---- */
  --color-text-primary: #332F3A;
  --color-text-muted: #635F69;
  --color-text-light: #9D99A5;

  /* ---- Accent Colors (Candy Shop Palette) ---- */
  --color-accent-violet: #7C3AED;
  --color-accent-violet-light: #A78BFA;
  --color-accent-violet-dark: #5B21B6;
  --color-accent-pink: #DB2777;
  --color-accent-pink-light: #F472B6;
  --color-accent-blue: #0EA5E9;
  --color-accent-blue-light: #38BDF8;
  --color-accent-green: #10B981;
  --color-accent-green-light: #34D399;
  --color-accent-amber: #F59E0B;
  --color-accent-amber-light: #FCD34D;

  /* ---- Status Colors ---- */
  --color-success: #10B981;
  --color-warning: #F59E0B;
  --color-error: #EF4444;
  --color-info: #0EA5E9;

  /* ---- Card Backgrounds ---- */
  --color-card-glass: rgba(255, 255, 255, 0.6);
  --color-card-solid: #FFFFFF;
  --color-card-dark: rgba(51, 47, 58, 0.9);

  /* ---- Font Families ---- */
  --font-display: 'Nunito', system-ui, sans-serif;
  --font-body: 'DM Sans', system-ui, sans-serif;

  /* ---- 4-Layer Clay Shadow Stacks ---- */

  /* Card Shadow - Floating effect */
  --shadow-clay-card:
    0 2px 4px rgba(124, 58, 237, 0.04),
    0 4px 8px rgba(124, 58, 237, 0.06),
    0 8px 16px rgba(124, 58, 237, 0.08),
    0 16px 32px rgba(124, 58, 237, 0.10);

  /* Card Hover Shadow - Enhanced float */
  --shadow-clay-card-hover:
    0 4px 8px rgba(124, 58, 237, 0.06),
    0 8px 16px rgba(124, 58, 237, 0.08),
    0 16px 32px rgba(124, 58, 237, 0.12),
    0 32px 64px rgba(124, 58, 237, 0.14);

  /* Button Shadow - Convex bulge */
  --shadow-clay-button:
    0 2px 4px rgba(124, 58, 237, 0.08),
    0 4px 8px rgba(124, 58, 237, 0.12),
    0 8px 16px rgba(124, 58, 237, 0.08),
    inset 0 1px 0 rgba(255, 255, 255, 0.2);

  /* Button Hover Shadow */
  --shadow-clay-button-hover:
    0 4px 8px rgba(124, 58, 237, 0.12),
    0 8px 16px rgba(124, 58, 237, 0.16),
    0 12px 24px rgba(124, 58, 237, 0.12),
    inset 0 1px 0 rgba(255, 255, 255, 0.3);

  /* Pressed Shadow - Recessed/Concave */
  --shadow-clay-pressed:
    0 1px 2px rgba(124, 58, 237, 0.12),
    inset 0 2px 4px rgba(0, 0, 0, 0.06);

  /* Input Shadow - Subtle inset */
  --shadow-clay-input:
    inset 0 2px 4px rgba(124, 58, 237, 0.06),
    0 1px 0 rgba(255, 255, 255, 0.8);

  /* Input Focus Shadow */
  --shadow-clay-input-focus:
    inset 0 2px 4px rgba(124, 58, 237, 0.04),
    0 0 0 4px rgba(124, 58, 237, 0.1);

  /* ---- Transition Durations ---- */
  --duration-fast: 150ms;
  --duration-normal: 250ms;
  --duration-slow: 400ms;

  /* ---- Animation Easings ---- */
  --ease-bounce: cubic-bezier(0.34, 1.56, 0.64, 1);
  --ease-smooth: cubic-bezier(0.4, 0, 0.2, 1);
}

/* ========================================
   BASE LAYER STYLES
   ======================================== */

@layer base {
  * {
    font-family: var(--font-body);
  }

  html {
    background-color: var(--color-canvas);
    color: var(--color-text-primary);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  body {
    margin: 0;
    min-height: 100vh;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-display);
    font-weight: 700;
    color: var(--color-text-primary);
  }

  /* Remove default button styles */
  button {
    font-family: inherit;
    border: none;
    background: none;
    cursor: pointer;
  }

  /* Remove default input styles */
  input, textarea, select {
    font-family: inherit;
    border: none;
    outline: none;
    background: none;
  }

  /* Focus visible for accessibility */
  :focus-visible {
    outline: 2px solid var(--color-accent-violet);
    outline-offset: 2px;
  }
}

/* ========================================
   FLOATING BLOB ANIMATIONS
   Zero-gravity drift effect
   ======================================== */

@keyframes blob {
  0%, 100% {
    transform: translate(0, 0) scale(1);
  }
  25% {
    transform: translate(20px, -30px) scale(1.05);
  }
  50% {
    transform: translate(-20px, 20px) scale(0.95);
  }
  75% {
    transform: translate(30px, 10px) scale(1.02);
  }
}

@keyframes blob-reverse {
  0%, 100% {
    transform: translate(0, 0) scale(1);
  }
  25% {
    transform: translate(-25px, 20px) scale(0.98);
  }
  50% {
    transform: translate(15px, -25px) scale(1.04);
  }
  75% {
    transform: translate(-20px, -10px) scale(1.01);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-20px);
  }
}

@keyframes breathe {
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.02);
  }
}

/* ========================================
   UTILITY CLASSES
   ======================================== */

@layer utilities {
  /* Animation Classes */
  .animate-blob {
    animation: blob 20s ease-in-out infinite;
  }

  .animate-blob-reverse {
    animation: blob-reverse 22s ease-in-out infinite;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-breathe {
    animation: breathe 4s ease-in-out infinite;
  }

  /* Animation Delays */
  .animation-delay-1000 {
    animation-delay: 1s;
  }

  .animation-delay-2000 {
    animation-delay: 2s;
  }

  .animation-delay-3000 {
    animation-delay: 3s;
  }

  .animation-delay-4000 {
    animation-delay: 4s;
  }

  .animation-delay-6000 {
    animation-delay: 6s;
  }

  /* Font utilities */
  .font-display {
    font-family: var(--font-display);
  }

  .font-body {
    font-family: var(--font-body);
  }

  /* Glass effect */
  .glass {
    background: rgba(255, 255, 255, 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
  }

  /* Clay gradient backgrounds */
  .bg-gradient-violet {
    background: linear-gradient(135deg, var(--color-accent-violet-light) 0%, var(--color-accent-violet) 100%);
  }

  .bg-gradient-pink {
    background: linear-gradient(135deg, var(--color-accent-pink-light) 0%, var(--color-accent-pink) 100%);
  }

  .bg-gradient-blue {
    background: linear-gradient(135deg, var(--color-accent-blue-light) 0%, var(--color-accent-blue) 100%);
  }

  .bg-gradient-green {
    background: linear-gradient(135deg, var(--color-accent-green-light) 0%, var(--color-accent-green) 100%);
  }

  /* Text gradient */
  .text-gradient {
    background: linear-gradient(135deg, var(--color-accent-violet) 0%, var(--color-accent-pink) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

/* ========================================
   REDUCED MOTION
   Accessibility support
   ======================================== */

@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

