import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../context/AuthContext'
import { PageContainer } from '../components/layout'
import { Card, Input, Button } from '../components/ui'

export default function Login() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [isSignUp, setIsSignUp] = useState(false)
  const [error, setError] = useState('')
  const [loading, setLoading] = useState(false)

  const { signIn, signUp } = useAuth()
  const navigate = useNavigate()

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError('')
    setLoading(true)

    try {
      const { error } = isSignUp
        ? await signUp(email, password)
        : await signIn(email, password)

      if (error) {
        setError(error.message)
      } else {
        navigate('/dashboard')
      }
    } catch (err) {
      setError('An unexpected error occurred')
    } finally {
      setLoading(false)
    }
  }

  return (
    <PageContainer centered>
      <div className="w-full max-w-md px-4">
        <Card className="text-center" hover={false}>
          {/* Logo/Icon */}
          <div className="mb-6 flex justify-center">
            <div className="
              w-20 h-20
              rounded-[24px]
              bg-gradient-to-br from-accent-violet-light to-accent-violet
              shadow-[var(--shadow-clay-button)]
              flex items-center justify-center
              animate-breathe
            ">
              <svg
                className="w-10 h-10 text-white"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"
                />
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
            </div>
          </div>

          {/* Title */}
          <h1 className="text-3xl md:text-4xl font-display font-black text-text-primary mb-2">
            RallyCoach
          </h1>
          <p className="text-text-muted mb-8">
            AI-Powered Badminton Analysis
          </p>

          {/* Mode Badge */}
          <div className="inline-flex mb-8">
            <span className={`
              px-4 py-2 rounded-full font-display font-bold text-sm
              transition-all duration-300
              ${isSignUp
                ? 'bg-accent-pink/15 text-accent-pink'
                : 'bg-accent-violet/15 text-accent-violet'
              }
            `}>
              {isSignUp ? 'Create Account' : 'Welcome Back'}
            </span>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            <Input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <Input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            {error && (
              <div className="
                p-4 rounded-[16px]
                bg-error/10 border border-error/20
                text-error text-sm font-medium
              ">
                {error}
              </div>
            )}

            <Button
              type="submit"
              disabled={loading}
              loading={loading}
              className="w-full mt-2"
              size="lg"
            >
              {isSignUp ? 'Create Account' : 'Sign In'}
            </Button>
          </form>

          {/* Toggle */}
          <p className="mt-8 text-text-muted">
            {isSignUp ? 'Already have an account?' : "Don't have an account?"}
            <button
              onClick={() => setIsSignUp(!isSignUp)}
              className="
                ml-2 font-bold text-accent-violet
                hover:text-accent-violet-dark
                transition-colors duration-200
              "
            >
              {isSignUp ? 'Sign In' : 'Sign Up'}
            </button>
          </p>
        </Card>

        {/* Footer text */}
        <p className="text-center mt-6 text-text-light text-sm">
          Improve your game with AI-powered video analysis
        </p>
      </div>
    </PageContainer>
  )
}

