import { useState, useRef } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../context/AuthContext'
import { PageContainer, Header } from '../components/layout'
import { Card, Button, Progress } from '../components/ui'

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || 'http://localhost:8000'

export default function Upload() {
  const [videoFile, setVideoFile] = useState(null)
  const [uploading, setUploading] = useState(false)
  const [progress, setProgress] = useState('')
  const [dragActive, setDragActive] = useState(false)
  const fileInputRef = useRef(null)

  const { user, signOut } = useAuth()
  const navigate = useNavigate()

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setVideoFile(file)
    }
  }

  const handleDrag = (e) => {
    e.preventDefault()
    e.stopPropagation()
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true)
    } else if (e.type === 'dragleave') {
      setDragActive(false)
    }
  }

  const handleDrop = (e) => {
    e.preventDefault()
    e.stopPropagation()
    setDragActive(false)
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setVideoFile(e.dataTransfer.files[0])
    }
  }

  const handleAnalyze = async () => {
    if (!videoFile) return

    setUploading(true)
    setProgress('Uploading video...')

    try {
      const formData = new FormData()
      formData.append('file', videoFile)
      if (user) {
        formData.append('user_id', user.id)
      }

      setProgress('Analyzing video (this may take a minute)...')

      const response = await fetch(`${BACKEND_URL}/api/analyze`, {
        method: 'POST',
        body: formData,
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.detail || 'Analysis failed')
      }

      const resultData = await response.json()
      setProgress('Analysis complete!')

      navigate('/results', { state: { analysis: resultData } })
    } catch (error) {
      alert('Analysis failed: ' + error.message)
      setProgress('')
    } finally {
      setUploading(false)
    }
  }

  const handleSignOut = async () => {
    await signOut()
    navigate('/')
  }

  return (
    <PageContainer>
      <div className="max-w-4xl mx-auto px-4 py-6">
        <Header
          title="Upload Video"
          subtitle="Get AI-powered coaching feedback on your technique"
          onBack={() => navigate('/dashboard')}
          actions={
            user && (
              <Button variant="ghost" onClick={handleSignOut}>
                Sign Out
              </Button>
            )
          }
        />

        <div className="mt-8">
          <Card hover={false} className="max-w-2xl mx-auto">
            {/* Upload Icon */}
            <div className="flex justify-center mb-6">
              <div className={`
                w-24 h-24 rounded-[28px]
                flex items-center justify-center
                transition-all duration-300
                ${dragActive
                  ? 'bg-gradient-to-br from-accent-violet-light to-accent-violet scale-110'
                  : 'bg-gradient-to-br from-accent-blue-light to-accent-blue'
                }
                shadow-[var(--shadow-clay-button)]
              `}>
                <svg
                  className="w-12 h-12 text-white"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"
                  />
                </svg>
              </div>
            </div>

            <h2 className="text-2xl font-display font-bold text-center text-text-primary mb-2">
              Upload Your Match Video
            </h2>
            <p className="text-center text-text-muted mb-8">
              Drag and drop or click to select a video file
            </p>

            {/* Drop Zone */}
            <div
              className={`
                relative rounded-[24px] border-2 border-dashed
                p-8 mb-6 text-center cursor-pointer
                transition-all duration-300
                ${dragActive
                  ? 'border-accent-violet bg-accent-violet/5 scale-[1.02]'
                  : 'border-accent-violet/30 bg-white/40 hover:border-accent-violet/50 hover:bg-white/60'
                }
                ${uploading ? 'pointer-events-none opacity-60' : ''}
              `}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
              onClick={() => fileInputRef.current?.click()}
            >
              <input
                ref={fileInputRef}
                type="file"
                accept="video/*"
                onChange={handleFileChange}
                className="hidden"
                disabled={uploading}
              />

              {videoFile ? (
                <div className="space-y-3">
                  <div className="
                    inline-flex items-center gap-3 px-4 py-3
                    bg-accent-green/10 rounded-[16px]
                    border border-accent-green/20
                  ">
                    <svg
                      className="w-6 h-6 text-accent-green"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                      />
                    </svg>
                    <span className="font-medium text-accent-green">
                      {videoFile.name}
                    </span>
                  </div>
                  <p className="text-sm text-text-muted">
                    {(videoFile.size / (1024 * 1024)).toFixed(2)} MB
                  </p>
                </div>
              ) : (
                <div className="space-y-2">
                  <p className="text-text-primary font-medium">
                    Drop your video here
                  </p>
                  <p className="text-sm text-text-muted">
                    Supports MP4, MOV, AVI, and other video formats
                  </p>
                </div>
              )}
            </div>

            {/* Progress */}
            {progress && (
              <div className="mb-6">
                <Progress
                  value={progress.includes('complete') ? 100 : progress.includes('Analyzing') ? 60 : 30}
                  label={progress}
                  variant={progress.includes('complete') ? 'green' : 'violet'}
                />
              </div>
            )}

            {/* Action Button */}
            <Button
              onClick={handleAnalyze}
              disabled={!videoFile || uploading}
              loading={uploading}
              className="w-full"
              size="lg"
            >
              {uploading ? 'Analyzing...' : 'Start Analysis'}
            </Button>

            {/* Tips */}
            <div className="mt-8 pt-6 border-t border-white/40">
              <h3 className="text-sm font-display font-bold text-text-muted mb-3">
                Tips for best results:
              </h3>
              <ul className="space-y-2 text-sm text-text-muted">
                <li className="flex items-start gap-2">
                  <span className="text-accent-violet">•</span>
                  Record from a side angle for clear stroke visibility
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-accent-violet">•</span>
                  Ensure good lighting and a stable camera
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-accent-violet">•</span>
                  Include multiple rallies for comprehensive analysis
                </li>
              </ul>
            </div>
          </Card>
        </div>
      </div>
    </PageContainer>
  )
}

