      <h3 className="text-lg font-display font-bold text-text-primary mb-2">
        Something went wrong
      </h3>
      <p className="text-text-muted">{message}</p>
    </Card>
  )
}

function EmptyState({ onUpload }) {
  return (
    <Card variant="solid" hover={false} className="max-w-lg mx-auto text-center">
      <div className="
        w-24 h-24 rounded-[28px] mx-auto mb-6
        bg-gradient-to-br from-accent-violet-light/20 to-accent-pink-light/20
        flex items-center justify-center
      ">
        <svg className="w-12 h-12 text-accent-violet" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
        </svg>
      </div>
      <h2 className="text-2xl font-display font-black text-text-primary mb-2">
        No videos yet
      </h2>
      <p className="text-text-muted mb-6">
        Upload your first badminton video to get AI-powered coaching feedback!
      </p>
      <Button onClick={onUpload} size="lg">
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
        </svg>
        Upload Your First Video
      </Button>
    </Card>
  )
}

function AnalysisCard({ analysis, formatDate, onView, onDelete }) {
  const shots = analysis.result_json?.shots || []
  const feedback = analysis.result_json?.feedback || analysis.result_json?.gemini_feedback

  return (
    <Card className="flex flex-col">
      {/* Header */}
      <div className="flex items-start justify-between mb-4 pb-4 border-b border-white/40">
        <div>
          <h3 className="font-display font-bold text-text-primary">
            Analysis #{analysis.id.slice(0, 8)}
          </h3>
          <p className="text-sm text-text-muted mt-1">
            {formatDate(analysis.created_at)}
          </p>
        </div>
        <div className="
          w-10 h-10 rounded-[12px]
          bg-gradient-to-br from-accent-blue-light to-accent-blue
          flex items-center justify-center
          shadow-[var(--shadow-clay-button)]
        ">
          <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
          </svg>
        </div>
      </div>

      {/* Stats */}
      <div className="flex-1">
        {shots.length > 0 && (
          <>
            <div className="flex items-center justify-between mb-3">
              <span className="text-text-muted">Shots Detected</span>
              <span className="font-display font-black text-2xl text-accent-violet">
                {shots.length}
              </span>
            </div>

            {/* Shot badges */}
            <div className="flex flex-wrap gap-2 mb-4">
              {shots.slice(0, 5).map((shot, idx) => (
                <Badge
                  key={idx}
                  variant={shot.pose_errors?.length > 0 ? 'error' : 'success'}
                  size="sm"
                >
                  {shot.shot_type}
                </Badge>
              ))}
              {shots.length > 5 && (
                <Badge variant="neutral" size="sm">
                  +{shots.length - 5} more
                </Badge>
              )}
            </div>
          </>
        )}

        {/* Feedback preview */}
        {feedback && (
          <div className="
            p-3 rounded-[16px]
            bg-white/40
            border border-white/40
          ">
            <p className="text-sm text-text-muted line-clamp-2">
              {typeof feedback === 'string'
                ? feedback.slice(0, 100)
                : feedback.technique?.slice(0, 100)
              }
              {((typeof feedback === 'string' && feedback.length > 100) ||
                (feedback.technique?.length > 100)) && '...'}
            </p>
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="flex gap-3 mt-4 pt-4 border-t border-white/40">
        <Button
          variant="primary"
          size="sm"
          className="flex-1"
          onClick={onView}
        >
          View Details
        </Button>
        <Button
          variant="ghost"
          size="sm"
          onClick={onDelete}
          className="text-error hover:bg-error/10"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
          </svg>
        </Button>
      </div>
    </Card>
  )
}

